import React, { useEffect } from 'react';
import { Carousel } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import { getCarouselAction } from '../../../../redux/actions/CarouselAction';
const contentStyle = {
    height: '700px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    backgroundPosition: 'center',
    backgroundSize: '100%',
    backgroundRepeat: 'no-repeat'
};
export default function HomeCarousel() {
    const { listBanner } = useSelector(state => state.CarouselReducer);
    const dispatch = useDispatch();
    //tự kích hoạt khi component load ra
    useEffect(() => {
        dispatch(getCarouselAction);
    }, []);
    const renderImg = () => {
        return listBanner.map((item, index) => {
            return <div key={index}>
                <div style={{ ...contentStyle, backgroundImage: `url(${item.hinhAnh})` }}>
                    {/* <img src={item.hinhAnh} className="w-full opacity-0" alt={item.hinhAnh} /> */}
                </div>
            </div>;
        });
    };
    return (
        <Carousel effect="fade" className='w-full'>
            {renderImg()}
        </Carousel>
    );
}
