import React from 'react';
import { Carousel } from 'antd';

export default function Mobile() {
    const contentStyle = {
        height: '100%',
        color: '#fff',
        lineHeight: '160px',
        textAlign: 'center',
        background: 'black',
    };
    const renderImg = () => {
        let arr = [];
        for (let i = 1; i <= 16; i++) {
            arr.push(<img src={`https://tix.vn/app/assets/img/icons/slide/slide${i}.jpg`} alt='123' />);


        }
        return arr.map((item, index) => {
            return <div className='w-full h-full overflow-hidden '>
                {item}
            </div>;
        });
    };
    return (
        <div id='homeApp'>
            <div className='main-mobile'>
                <div className='grid grid-cols-2 gap-5 text-white px-20'>
                    <div className='left text-center'>
                        <p class="textLeft">ỨNG DỤNG ĐẶT VÉ XEM PHIM TIỆN LỢI ĐÃ CÓ TRÊN SMARTPHONE</p>
                        <p><p class="MuiTypography-root jss148 MuiTypography-body1">Không chỉ đặt vé, bạn còn có thể bình luận phim, chấm điểm rạp và đổi quà hấp dẫn.</p></p>

                    </div>
                    <div className='right' >
                        <img class="img-responsive phone-img" src="https://tix.vn/app/assets/img/icons/mobile.png" alt="Loading..."></img>

                        <Carousel autoplay dots={false} className='sliderScreen h-full overflow-hidden' >

                            {renderImg()}
                        </Carousel>


                    </div>
                </div>
            </div>
        </div>

    );
}
