import { history } from "../../App";
import { quanLyNguoiDungService } from "../../service/QuanLyNguoiDungService";
import { DANG_NHAP_ACTION, SET_DANH_SACH_NGUOI_DUNG, SET_THONG_TIN_NGUOI_DUNG } from "../types/QuanLyNguoiDungType";
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

export const dangNhapAction = (thongtinDangNhap) => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.dangNhap(thongtinDangNhap);
            if (result.status === 200) {
                dispatch({
                    type: DANG_NHAP_ACTION,
                    thongTinDangnhap: result.data.content
                });

            }
            Swal.fire({
                title: 'Error!',
                text: 'error',
                icon: 'error',
                confirmButtonText: 'Cool'
            });

        } catch (error) {
            console.log(error);
        }
    };

};

export const dangNhapAdminAction = (thongtinDangNhap) => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.dangNhap(thongtinDangNhap);
            if (result.status === 200) {
                dispatch({
                    type: DANG_NHAP_ACTION,
                    thongTinDangnhap: result.data.content
                });
                Swal.fire({
                    title: 'How old are you?',
                    icon: 'question',
                    input: 'range',
                    inputLabel: 'Your age',
                    inputAttributes: {
                        min: 8,
                        max: 120,
                        step: 1
                    },
                    inputValue: 25
                });
                history.push('/mainAdmin');
            }
        } catch (error) {
            console.log(error);
        }
    };
};
export const thongTinTaiKhoanAction = () => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.LayThongTinTaiKhoan();
            if (result.status === 200) {
                dispatch({
                    type: SET_THONG_TIN_NGUOI_DUNG,
                    thongTinTaiKhoan: result.data.content
                });
            }
        } catch (error) {
            console.log(error);
        }
    };
};

export const layDanhSachNguoiDungAction = (tukhoa = '') => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.layDanhSachNguoiDung(tukhoa);
            console.log("file: QuanLyNguoiDungAction.js ~ line 58 ~ return ~ result", result);
            if (result.status === 200) {
                dispatch({
                    type: SET_DANH_SACH_NGUOI_DUNG,
                    danhSachNguoiDung: result.data.content
                });
            }
        } catch (error) {
            console.log(error);
        }
    };
};

export const layDanhSachLoaiNguoiDungAction = () => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.LayDanhSachLoaiNguoiDung();
            if (result.status === 200) {
                dispatch({
                    type: "SET_LOAI_NGUOI_DUNG",
                    loaiNguoiDung: result.data.content
                });
            }
        } catch (error) {
            console.log(error);
        }
    };
};

export const CapNhatNguoiDungAction = (userEdit) => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.CapNhatNguoiDung(userEdit);
            console.log("file: QuanLyNguoiDungAction.js ~ line 91 ~ return ~ result", result);
            if (result.status === 200) {
                alert('cập nhật thành công');
            }
        } catch (error) {
            console.log(error);
        }
    };
};
export const ThemNguoiDungAction = (user) => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.ThemNguoiDung(user);
            console.log("file: QuanLyNguoiDungAction.js ~ line 104 ~ return ~ result", result);
            if (result.status === 200) {
                alert('Thêm người dùng thành công!');
            }
        } catch (error) {
            console.log(error);
        }
    };
};
