import React, { useEffect } from 'react';
import { Carousel } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import { getCarouselAction } from '../../../../redux/actions/CarouselAction';
import OwlCarousel from 'react-owl-carousel2';
import 'react-owl-carousel2/lib/styles.css';
import 'react-owl-carousel2/src/owl.theme.default.css';
import { layDanhSachPhimAction } from '../../../../redux/actions/QuanLyPhimAction';

const dataCarousel = [
    {
        "maPhim": 6375,
        "tenPhim": "Black Panther",
        "biDanh": "black-panther",
        "trailer": "https://www.youtube.com/watch?v=rrwBnlYOp4g",
        "hinhAnh": "http://movieapi.cyberlearn.vn/hinhanh/black-panther_gp06.jpg",
        "banner": "/images/black-banner.png",
        "moTa": "Black Panther is a 2018 American superhero film based on the Marvel Comics character of the same name. Produced by Marvel Studios and distributed by Walt Disney Studios Motion Pictures, it is the 18th film in the Marvel Cinematic Universe (MCU). The film was directed by Ryan Coogler, who co-wrote the screenplay with Joe Robert Cole, and it stars Chadwick Boseman as T'Challa / Black Panther alongside Michael B. Jordan.",
        "maNhom": "GP06",
        "ngayKhoiChieu": "2021-01-23T00:00:00",
        "danhGia": 10,
        "hot": null,
        "dangChieu": null,
        "sapChieu": null
    },
    {
        "maPhim": 8528,
        "tenPhim": "Transformers The Last",
        "biDanh": "transformers-the-last",
        "trailer": "https://www.youtube.com/embed/6Vtf0MszgP8",
        "hinhAnh": "http://movieapi.cyberlearn.vn/hinhanh/transformers-the-last_gp09.jpg",
        "banner": "images/transformer-banner.jpg",
        "moTa": "Robot Đại Chiến 5: Chiến Binh Cuối Cùng là phần tiếp theo về Robot biến hình ăn khách. \"Chiến Binh Cuối Cùng\" phá nát những huyền thoại cốt lõi của loạt phim Transformers, và tái định nghĩa thế nào là anh hùng. Con người và các Transformer đang có chiến tranh, Optimus Prime đã biến mất. Chìa khóa để cứu tương lai của chúng ta đang được chôn vùi trong những bí mật của quá khứ, trong lịch sử ẩn còn được giữ kín của các Transformer trên Trái Đất.",
        "maNhom": "GP09",
        "ngayKhoiChieu": "2022-01-15T00:00:00",
        "danhGia": 10,
        "hot": null,
        "dangChieu": null,
        "sapChieu": null

    },
    {
        "maPhim": 1415,
        "tenPhim": "Jurassic World",
        "biDanh": "jurassic-world",
        "trailer": "https://www.youtube.com/embed/RFinNxS5KN4",
        "hinhAnh": "http://movieapi.cyberlearn.vn/hinhanh/jurassicworld.jpg",
        banner: 'https://image.api.playstation.com/vulcan/ap/rnd/202107/2708/WZhQmSMCGEJgLHhnikOcuMnu.png',
        "moTa": "A new theme park is built on the original site of Jurassic Park. Everything is going well until the park's newest attraction--a genetically modified giant stealth killing machine--escapes containment and goes on a killing spree.",
        "maNhom": "GP12",
        "ngayKhoiChieu": "2019-07-29T00:00:00",
        "danhGia": 5,
        "hot": false,
        "dangChieu": true,
        "sapChieu": false
    },

];
export default function HomeCarousel() {
    const { arrDefault } = useSelector(state => state.QuanLyPhimReducer);
    console.log("file: HomeCarousel.js ~ line 21 ~ HomeCarousel ~ arrDefault", arrDefault);
    const dispatch = useDispatch();
    //tự kích hoạt khi component load ra
    useEffect(() => {
        dispatch(layDanhSachPhimAction());
    }, []);
    const renderImg = () => {
        return dataCarousel.map((film, index) => {
            return <div className="hero-slide-item">
                <img src={film.banner} alt />
                <div className="overlay" />
                <div className="hero-slide-item-content">
                    <div className="item-content-wraper">
                        <div className="item-content-title top-down">
                            {film.tenPhim}
                        </div>
                        <div className="movie-infos top-down delay-2">
                            <div className="movie-info">
                                <i className="bx bxs-star" />
                                <span>{film.danhGia}</span>
                            </div>
                            <div className="movie-info">
                                <i className="bx bxs-time" />
                                <span>{film.time} mins</span>
                            </div>
                            <div className="movie-info">
                                <span>HD</span>
                            </div>
                            <div className="movie-info">
                                <span>16+</span>
                            </div>
                        </div>
                        <div className="item-content-description top-down delay-4">
                            {film.moTa}
                        </div>
                        <div className="item-action top-down delay-6">
                            <a href="#" className="btn btn-hover">
                                <i className="bx bxs-right-arrow" />
                                <span>Watch now</span>
                            </a>
                        </div>
                    </div>
                </div>
            </div>;
        });
    };
    let navText = [ "<i class='bx bx-chevron-left'></i>", "<i class='bx bx-chevron-right'></i>" ];
    const options = {
        items: 1,
        dots: false,
        loop: true,
        nav: true,
        navText: navText,
        autoplay: true,
        autoplayHoverPause: true
    };
    return (
        <div className="hero-section"  >
            {/* HERO SLIDE */}
            <div className="hero-slide">
                <OwlCarousel className="owl-carousel carousel-nav-center" options={options} id="hero-carousel">
                    {renderImg()}
                </OwlCarousel>
            </div>

        </div>

        // <Carousel effect="fade">
        //     {renderImg()}
        // </Carousel>
    );
}
