import { CHUYEN_TAB, DAT_GHE, DAT_VE_HOAN_TAT, SET_CHI_TIET_PHONG_VE } from "../types/QuanLyDatVeType";

const initialState = {
    chiTietPhongVe: {},
    danhSachGheDangDat: [

    ],
    danhSachGheKhachDat: [],
    tabActive: '1'
};


export default (state = initialState, action) => {
    switch (action.type) {
        case SET_CHI_TIET_PHONG_VE: {
            return { ...state, chiTietPhongVe: action.chiTietPhongVe };
        }
        case DAT_GHE: {
            //cập nhật danh sách ghế đang đặt
            let index = state.danhSachGheDangDat.findIndex(gheDD => gheDD.maGhe === action.gheDuocChon.maGhe);
            if (index != -1) {
                state.danhSachGheDangDat.splice(index, 1);
            } else {
                state.danhSachGheDangDat.push(action.gheDuocChon);
            }
            return { ...state };
        }
        case DAT_VE_HOAN_TAT: {
            state.danhSachGheDangDat = [];
            return { ...state };
        }
        case CHUYEN_TAB: {
            return { ...state, tabActive: "2" };
        }
        case "CHANGE_TAB": {
            return { ...state, tabActive: action.key };
        }
        case 'GHE_KHACH_DAT': {
            return { ...state, danhSachGheKhachDat: action.arrGheKhachDat };
        }

        default:
            return state;
    }
};
