import React, { Fragment, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { layChiTietPhongVeAction } from '../../redux/actions/QuanLyDatVeAction';
import { CloseOutlined } from '@ant-design/icons';
import style from './Checkout.module.css';
import './Checkout.css';
import _ from 'lodash';
import { DAT_GHE } from '../../redux/types/QuanLyDatVeType';
export default function Checkout(props) {
    const { userLogin } = useSelector(state => state.QuanLyNguoiDungReducer);
    const { chiTietPhongVe, danhSachGheDangDat } = useSelector(state => state.QuanLyDatVeReducer);
    console.log("file: Checkout.js ~ line 10 ~ Checkout ~ chiTietPhongVe", chiTietPhongVe);
    const { thongTinPhim, danhSachGhe } = chiTietPhongVe;
    const dispatch = useDispatch();
    const renderSeats = () => {
        return danhSachGhe?.map((ghe, index) => {
            let classGheVip = ghe.loaiGhe === 'Vip' ? 'gheVip' : '';
            let classGheDaDat = ghe.daDat === true ? 'gheDaDat' : '';
            let classGheDangDat = '';
            let indexGheDD = danhSachGheDangDat.findIndex(gheDD => gheDD.maGhe === ghe.maGhe);
            if (indexGheDD != -1) {
                classGheDaDat = 'gheDangDat';
            }
            return <Fragment>

                <button disabled={ghe.daDat} className={`ghe font-semibold ${classGheVip} ${classGheDaDat} ${classGheDangDat}`} key={index} onClick={() => {
                    dispatch({
                        type: DAT_GHE,
                        gheDuocChon: ghe
                    });
                }}>
                    {ghe.daDat ? <CloseOutlined /> : ghe.stt}

                </button>
                {(index + 1) % 13 === 0 ? <br /> : ''}
            </Fragment>;
        });
    };
    useEffect(() => {
        dispatch(layChiTietPhongVeAction(props.match.params.id));
    }, []);
    return (
        <div className='min-h-screen mt-5 '>
            <div className='grid grid-cols-12'>
                <div className='col-span-9'>
                    <div className='flex flex-col items-center mt-5'>
                        <div className='bg-black' style={{ width: '80%', height: 15 }}>

                        </div>
                        <div className={`${style[ 'trapezoid' ]} text-center`}>
                            <h1 className='text-gray-900 mt-3' >Màn hình</h1>
                        </div>
                        <div>
                            {renderSeats()}
                        </div>
                    </div>
                </div>
                <div className='col-span-3 font-semibold '>
                    <h3 className='text-green-400 text-center text-2xl'>0 đ</h3>
                    <hr />
                    <h3 className='text-xl mt-2'>{thongTinPhim?.tenPhim}</h3>
                    <p>Địa điểm: {thongTinPhim?.diaChi}</p>
                    <p>Ngày chiếu: {thongTinPhim?.ngayChieu} - {thongTinPhim?.gioChieu} {thongTinPhim?.tenRap}</p>
                    <hr />
                    <div className='grid grid-cols-2 my-5'>
                        <div className='col-span-1'>
                            <span className='text-red-400 text-lg mr-2'>Ghế:</span>
                            {_.sortBy(danhSachGheDangDat).map((gheDD, index) => {
                                return <span key={index} className='text-green-500 text-xl ml-2'>
                                    {gheDD.stt}
                                </span>;
                            })}
                        </div>
                        <div className='col-span-1 text-right text-green-400 text-lg'>
                            0đ
                        </div>
                    </div>
                    <hr />
                    <div className='my-5'>
                        <i>Email</i> <br />
                        {userLogin.email}
                    </div>
                    <hr />
                    <div className='my-5'>
                        <i>Phone</i> <br />
                        {userLogin.soDT}
                    </div>
                    <hr />
                    <div className='' style={{ marginBottom: 0 }}>
                        <div className='bg-green-500 text-white w-full text-center py-3 font-bold text-2xl'>
                            ĐẶT VÉ
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
