import React, { useEffect, useState } from 'react';
import { Form, Input, Button, Checkbox, Cascader, DatePicker, InputNumber, Select } from 'antd';
import { quanLyRapService } from '../../../service/QuanLyRapService';
import { useFormik } from 'formik';


export default function Showtime(props) {

    const [ state, setState ] = useState({
        heThongRapChieu: [],
        cumRapChieu: []
    });
    const formik = useFormik({
        initialValues: {
            maPhim: props.match.params.id,
            ngayChieuGioChieu: '',
            maRap: '',
            giaVe: ''
        },
        onSubmit: (values) => {
            console.log("values", values);
        }
    });
    useEffect(async () => {
        try {
            let result = await quanLyRapService.layThongTinHeThongRap();
            setState({
                ...state,
                heThongRapChieu: result.data.content
            });
        } catch (err) {

        }
    }, []);
    const handleChangeHeThongRap = async (values) => {
        try {
            let result = await quanLyRapService.layThongTinCumRapTheoHeThong(values);
            setState({
                ...state,
                cumRapChieu: result.data.content
            });
        } catch (error) {

        }

    };
    const handleChangecumrap = (values) => {

    };
    function onChangeDate(date, dateString) {
        formik.setFieldValue('ngayChieuGioChieu', dateString);
    }
    const onOk = () => {

    };
    const handleChangeInputNumber = (value) => {
        formik.setFieldValue('giaVe', value);
    };
    const revertHeThongRap = () => {
        return state.heThongRapChieu.map((htr, index) => {
            return { label: htr.tenHeThongRap, value: htr.maHeThongRap };
        });
    };
    return (
        <Form
            name="basic"
            initialValues={{ remember: true }}
            labelCol={{ span: 8 }}
            wrapperCol={{ span: 8 }}
            onSubmitCapture={formik.handleSubmit}
        >
            <h3>Tạo lịch chiếu</h3>
            <Form.Item
                label="Hệ thống rạp"

            >
                <Select options={revertHeThongRap()} placeholder="Please select" onChange={handleChangeHeThongRap} />
            </Form.Item>
            <Form.Item
                label="Cụm rạp"

            >
                <Select placeholder="Please select" options={state.cumRapChieu?.map((cumrap, index) => ({ label: cumrap.tenCumRap, value: cumrap.tenCumRap }))} onChange={handleChangecumrap} />
            </Form.Item>
            <Form.Item
                label="Ngày chiếu giờ chiếu"

            >
                <DatePicker format="DD/MM/YYYY hh:mm:ss" onChange={onChangeDate} showTime onOk={onOk} />
            </Form.Item>
            <Form.Item
                label="Ngày chiếu giờ chiếu"

            >
                <InputNumber min={75000} max={150000} onChange={handleChangeInputNumber} />
            </Form.Item>



            <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
                <Button type="primary" htmlType="submit">
                    Tạo lịch chiếu
                </Button>
            </Form.Item>
        </Form>
    );
}
