import React, { Fragment, useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import { history } from '../../../../App';
import { Select } from 'antd';
import { useTranslation } from 'react-i18next';
import { useDispatch, useSelector } from 'react-redux';
import _ from 'lodash';
import { Popover, Button } from 'antd';
import { UserOutlined } from '@ant-design/icons';

import { TOKEN, USER_LOGIN } from '../../../../util/settings/config';

const { Option } = Select;
export default function Header(props) {
    const [ toggle, setToggle ] = useState(false);
    const { userLogin } = useSelector(state => state.QuanLyNguoiDungReducer);
    const { t, i18n } = useTranslation();
    const dispatch = useDispatch();
    const handleChange = (e) => {
        i18n.changeLanguage(e.target.value);
    };

    const text = <div style={{ fontWeight: 700, textTransform: 'uppercase', textAlign: 'center' }}>
        <a>{t('profile.taikhoan')}</a>
    </div>;
    const content = (
        <div style={{ fontWeight: 700, textTransform: 'uppercase', textAlign: 'center' }} className='content-popover'>
            <p onClick={() => {
                history.push('/profile');
                dispatch({
                    type: 'CHANGE_KEY',
                    key: "1"
                });
            }}>{t('profile.thongtintaikhoan')}</p>
            <p onClick={() => {
                history.push('/profile');
                dispatch({
                    type: 'CHANGE_KEY',
                    key: "2"
                });
            }}>{t('profile.doimatkhau')}</p>
            <p onClick={() => {
                localStorage.removeItem(USER_LOGIN);
                localStorage.removeItem(TOKEN);
                history.push('/home');
                window.location.reload();
            }}>{t('profile.dangxuat')}</p>
        </div>
    );
    const renderLogin = () => {
        if (_.isEmpty(userLogin)) {
            return <Fragment>
                <li>
                    <a className="btn btn-hover" onClick={() => {
                        history.push('/login');
                    }}>
                        <span>{t('signin')}</span>
                    </a>
                </li>
                <li>
                    <a className="btn btn-hover" onClick={() => {
                        history.push('/register');
                    }}>
                        <span>{t('signup')}</span>
                    </a>
                </li>
            </Fragment>;
        }
        return <Fragment>
            <li>
                <Popover placement="bottom" title={text} content={content} trigger="click">
                    <a style={{ cursor: 'pointer' }} className='flex items-center'>
                        <UserOutlined className='font-bold text-2xl' /> <span style={{ fontWeight: 700, textTransform: 'uppercase' }}>{userLogin.taiKhoan}</span>
                    </a>
                </Popover>
            </li>
        </Fragment>;
    };

    return (
        <header className="nav-wrapper">
            <div className="container">
                <div className="nav">
                    <a href="#" className="logo">
                        <i className="bx bx-movie-play bx-tada main-color" />
                        T<span className="main-color">i</span>x
                    </a>
                    <ul className={`nav-menu pt-5 ${toggle ? 'active' : ''}`} id="nav-menu">
                        <li>
                            <a onClick={() => {
                                history.push('/');
                                window.scrollTo(0, 50);

                            }}>{t('home')}</a>
                        </li>
                        <li><a href="#movie_hot" onClick={() => {
                            history.push('/');
                        }}>{t('movie')}</a></li>
                        <li><a href="#theater" onClick={() => {
                            history.push('/');
                        }}>{t('theater')}</a></li>
                        <li><a href="https://www.facebook.com/profile.php?id=100006352638655" target='_blank'>{t('contact2')}</a></li>
                        {renderLogin()}
                        <li>
                            <select className='select-language' onChange={handleChange} defaultValue='vietnam'>
                                <option value="vietnam">
                                    {t('vietnamese')}
                                </option>
                                <option value="china"> {t('chinese')}</option>
                                <option value="english"> {t('english')}</option>
                            </select>

                        </li>
                    </ul>
                    <div className={`hamburger-menu ${toggle ? 'active' : ''}`} id="hamburger-menu" onClick={() => {
                        setToggle(!toggle);
                    }}>
                        <div className="hamburger" />
                    </div>
                </div>
            </div>
        </header>


    );
}
