import { quanLyRapService } from "../../service/QuanLyRapService";
import { SET_HE_THONG_RAP_CHIEU } from "../types/QuanLyRapType";

export const layDanhSachHeThongRapAction = () => {
    return async (dispatch) => {
        try {
            const result = await quanLyRapService.layThongTinLichChieuHeThongRap();
            if (result.status === 200) {
                dispatch({
                    type: SET_HE_THONG_RAP_CHIEU,
                    heThongRapChieu: result.data.content
                });
            }
        } catch (error) {

        }
    };
};
export const layThongTinChiTietPhim = () => {
    return async;
};
