import React, { Fragment, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { datVeAction, layChiTietPhongVeAction } from '../../redux/actions/QuanLyDatVeAction';
import { CloseOutlined, UserOutlined, CheckOutlined, AppleOutlined, AndroidOutlined } from '@ant-design/icons';
import { Tabs, Pagination } from 'antd';
import style from './Checkout.module.css';
import './Checkout.css';
import _ from 'lodash';
import { CHUYEN_TAB, DAT_GHE } from '../../redux/types/QuanLyDatVeType';
import { thongTinTaiKhoanAction } from '../../redux/actions/QuanLyNguoiDungAction';
import moment from 'moment';
function Checkout(props) {
    const { userLogin } = useSelector(state => state.QuanLyNguoiDungReducer);
    const { chiTietPhongVe, danhSachGheDangDat, danhSachGheKhachDat } = useSelector(state => state.QuanLyDatVeReducer);
    const { thongTinPhim, danhSachGhe } = chiTietPhongVe;
    console.log("file: Checkout.js ~ line 16 ~ Checkout ~ danhSachGhe", danhSachGhe);
    const dispatch = useDispatch();
    const renderSeats = () => {
        return danhSachGhe?.map((ghe, index) => {
            let classGheVip = ghe.loaiGhe === 'Vip' ? 'gheVip' : '';
            let classGheDaDat = ghe.daDat === true ? 'gheDaDat' : '';
            let classGheDangDat = '';
            let indexGheDD = danhSachGheDangDat.findIndex(gheDD => gheDD.maGhe === ghe.maGhe);
            if (indexGheDD != -1) {
                classGheDaDat = 'gheDangDat';
            }
            let classGheCuaMinh = '';
            if (userLogin.taiKhoan === ghe.taiKhoanNguoiDat) {
                classGheCuaMinh = 'gheCuaMinh';
            }
            //Kiểm tra ghế có được người khác đang đặt ko
            let classGheKhachDat = '';
            let indexGheKhachDat = danhSachGheKhachDat.findIndex(gheKD => gheKD.maGhe === ghe.maGhe);
            return <Fragment key={index}>

                <button disabled={ghe.daDat} className={`ghe font-semibold ${classGheVip} ${classGheDaDat} ${classGheDangDat}${classGheCuaMinh} ${classGheKhachDat}`} key={index} onClick={() => {
                    dispatch({
                        type: DAT_GHE,
                        gheDuocChon: ghe
                    });
                }}>
                    {ghe.taiKhoanNguoiDat === userLogin.taiKhoan ? <UserOutlined /> : ghe.daDat ? <CloseOutlined /> : ghe.stt}
                </button>
                {(index + 1) % 13 === 0 ? <br /> : ''}
            </Fragment>;
        });
    };
    useEffect(() => {
        dispatch(layChiTietPhongVeAction(props.match.params.id));
    }, []);
    return (
        <div className='min-h-screen mt-5 '>
            <div className='grid grid-cols-12'>
                <div className='col-span-9'>
                    <div className='flex flex-col items-center mt-5'>
                        <div className='bg-black' style={{ width: '80%', height: 15 }}>

                        </div>
                        <div className={`${style[ 'trapezoid' ]} text-center`}>
                            <h1 className='text-gray-900 mt-3' >Màn hình</h1>
                        </div>
                        <div>
                            {renderSeats()}
                        </div>
                        <div className='mt-5 flex justify-center w-full'>
                            <table className="min-w-full divide-y divide-gray-200">
                                <thead className="bg-gray-50 p-5">
                                    <tr>
                                        <th>Ghế chưa đặt</th>
                                        <th>Ghế đang đặt</th>
                                        <th>Ghế vip</th>
                                        <th>Ghế đã được đặt</th>
                                        <th>Ghế của bạn đặt</th>
                                        <th>Ghế người khác đang đặt</th>
                                    </tr>
                                </thead>
                                <tbody className="bg-white divide-y divide-gray-200">
                                    <tr>
                                        <td className='text-center'>
                                            <button className='ghe'><CheckOutlined /></button>
                                        </td>
                                        <td className='text-center'>
                                            <button className='ghe gheDangDat'><CheckOutlined /></button>
                                        </td>
                                        <td className='text-center'>
                                            <button className='ghe gheVip'><CheckOutlined /></button>
                                        </td>
                                        <td className='text-center'>
                                            <button className='ghe gheDaDat'> <CloseOutlined /></button>
                                        </td>
                                        <td className='text-center'>
                                            <button className='ghe gheCuaMinh'> <UserOutlined /></button>
                                        </td>
                                        <td className='text-center'>
                                            <button className='ghe gheKhachDat'> <CheckOutlined /></button>
                                        </td>

                                    </tr>
                                </tbody>
                            </table>


                        </div>
                    </div>
                </div>
                <div className='col-span-3 font-semibold '>
                    <h3 className='text-green-400 text-center text-2xl'> {danhSachGheDangDat.reduce((tongTien, ghe, index) => {
                        return tongTien += ghe.giaVe;
                    }, 0).toLocaleString()} VND</h3>
                    <hr />
                    <h3 className='text-xl mt-2'>{thongTinPhim?.tenPhim}</h3>
                    <p>Địa điểm: {thongTinPhim?.diaChi}</p>
                    <p>Ngày chiếu: {thongTinPhim?.ngayChieu} - {thongTinPhim?.gioChieu} {thongTinPhim?.tenRap}</p>
                    <hr />
                    <div className='grid grid-cols-2 my-5'>
                        <div className='col-span-2'>
                            <span className='text-red-400 text-lg mr-2'>Ghế:</span>

                        </div>
                        <div className='col-span-2'>
                            {_.sortBy(danhSachGheDangDat, [ 'stt' ]).map((gheDD, index) => {
                                return <span key={index} className='text-green-500 text-xl ml-2'>
                                    {gheDD.stt}
                                </span>;
                            })}
                        </div>
                        <div className='col-span-1 text-right text-green-400 text-lg'>
                            {danhSachGheDangDat.reduce((tongTien, ghe, index) => {
                                return tongTien += ghe.giaVe;
                            }, 0).toLocaleString()} VND
                        </div>
                    </div>
                    <hr />
                    <div className='my-5'>
                        <i>Email</i> <br />
                        {userLogin.email}
                    </div>
                    <hr />
                    <div className='my-5'>
                        <i>Phone</i> <br />
                        {userLogin.soDT}
                    </div>
                    <hr />
                    <div className='' style={{ marginBottom: 0 }}>
                        <div className='bg-green-500 text-white w-full text-center py-3 font-bold text-2xl cursor-pointer' onClick={() => {
                            let thongtinDatVe = {
                                maLichChieu: props.match.params.id,
                                danhSachVe: danhSachGheDangDat
                            };
                            dispatch(datVeAction(thongtinDatVe));
                        }}>
                            ĐẶT VÉ
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
function Ketqua(props) {
    const dispatch = useDispatch();
    const { thongTinTaiKhoan, userLogin } = useSelector(state => state.QuanLyNguoiDungReducer);
    console.log("file: Checkout.js ~ line 156 ~ Ketqua ~ userLogin", userLogin);
    console.log("file: Checkout.js ~ line 156 ~ Ketqua ~ thongTinTaiKhoan", thongTinTaiKhoan);
    useEffect(() => {
        dispatch(thongTinTaiKhoanAction());
    }, []);
    const renderLichSuDatVe = () => {
        return thongTinTaiKhoan.thongTinDatVe?.map((ve, index) => {
            return <div className="p-2 lg:w-1/3 md:w-1/2 w-full" key={index}>
                <div className="h-full flex items-center border-gray-200 border p-4 rounded-lg">
                    <img alt="team" className="w-16 h-16 bg-gray-100 object-cover object-center flex-shrink-0 rounded-full mr-4" src={ve.hinhAnh} />
                    <div className="flex-grow">
                        <h2 className="text-yellow-800 title-font font-medium text-xl">{ve.tenPhim}</h2>
                        <p>Ngày chiếu: {moment(ve.ngayDat).format('hh:mm A - DD-MM-YYYY')}</p>
                        <p className="text-gray-500 mb-0">Mã vé: {ve.maVe}</p>
                        <p className="text-gray-500">Giá vé: {ve.giaVe}</p>
                        <p>Tên hệ thống rạp: {_.first(ve.danhSachGhe).tenHeThongRap}</p>
                        <p>Tên cụm rạp: {_.first(ve.danhSachGhe).tenCumRap}</p>
                        <p>Tên ghế: {ve.danhSachGhe.map((ghe, index) => {
                            return <Fragment>
                                <span className='ml-2 text-red-600'>{ghe.tenGhe}</span>
                                {(index + 1) % 7 === 0 ? <br /> : ''}
                            </Fragment>;
                        })}</p>
                    </div>
                </div>
            </div>;
        });
    };
    return <div>
        <section className="text-gray-600 body-font">
            <div className="container px-5 py-24 mx-auto">
                <div className="flex flex-col text-center w-full mb-20">
                    <h1 className="sm:text-3xl text-2xl font-medium title-font mb-4 text-purple-600">Lịch sử đặt vé của khách hàng </h1>
                    <p>Họ tên : <span className='text-green-500'>{userLogin.hoTen}</span></p>
                    <p>Email : <span className='text-green-500'>{userLogin.email}</span></p>
                    <p>Số điện thoại : <span className='text-green-500'>{userLogin.soDT}</span></p>
                    <p className="lg:w-2/3 mx-auto leading-relaxed text-base">Chúc các bạn có một buổi xem phim vui vẻ</p>
                </div>
                <div className="flex flex-wrap -m-2">
                    {renderLichSuDatVe()}

                </div>
            </div>
        </section>;
    </div>;

}


const { TabPane } = Tabs;
export default function (props) {
    const { tabActive } = useSelector(state => state.QuanLyDatVeReducer);
    const dispatch = useDispatch();
    function callback(key) {
        dispatch({
            type: "CHANGE_TAB",
            key
        });
    }
    return <div className='p-5'>
        <Tabs activeKey={tabActive} onChange={callback}>
            <TabPane

                tab={
                    <span>
                        <AppleOutlined />
                        01 CHỌN GHẾ & THANH TOÁN
                    </span>
                }
                key="1"
            >
                <Checkout {...props} />
            </TabPane>
            <TabPane

                tab={
                    <span>
                        <AndroidOutlined />
                        02 KẾT QUẢ ĐẶT VÉ
                    </span>
                }
                key="2"
            >
                <Ketqua {...props} />
            </TabPane>
        </Tabs>
    </div>;
}
