const initialState = {
    isOpen: false,
    video: ""
};

export default (state = initialState, action) => {
    switch (action.type) {

        case "OPEN_VIDEO": {
            state.isOpen = true;
            state.video = action.video;
            return { ...state };
        }
        case "CLOSE_VIDEO": {
            state.isOpen = false;
            state.video = "";
            return { ...state };
        }


        default:
            return state;
    }
};
