import { GROUPID } from "../util/settings/config";
import { baseService } from "./baseService";


class QuanLyPhimService extends baseService {
    constructor() {
        super();
    }
    layDanhSachBanner = () => {
        return this.get(`/api/QuanLyPhim/LayDanhSachBanner`);
    };
    layDanhSachPhim = (tenPhim) => {
        if (tenPhim != '') {
            return this.get(`/api/QuanLyPhim/LayDanhSachPhim?maNhom=${GROUPID}&tenPhim=${tenPhim}`);
        }
        return this.get(`/api/QuanLyPhim/LayDanhSachPhim?maNhom=${GROUPID}`);
    };
    themPhimUploadHinh = (formData) => {
        return this.post(`/api/QuanLyPhim/ThemPhimUploadHinh`, formData);
    };
    LayThongTinPhim = (id) => {
        return this.get(`/api/QuanLyPhim/LayThongTinPhim?MaPhim=${id}`);
    };
    CapNhatThongTinPhim = (formData) => {
        return this.post(`/api/QuanLyPhim/CapNhatPhimUpload`, formData);
    };
    XoaPhim = (maPhim) => {
        return this.delete(`/api/QuanLyPhim/XoaPhim?MaPhim=${maPhim}`);
    };

}

export const quanLyPhimService = new QuanLyPhimService();
