import React, { useEffect, useState } from 'react';
import { Tabs, Radio, Space } from 'antd';
import { useFormik } from 'formik';
import { CapNhatNguoiDungAction, thongTinTaiKhoanAction } from '../../redux/actions/QuanLyNguoiDungAction';
import { useDispatch, useSelector } from 'react-redux';
import { GROUPID } from '../../util/settings/config';
import * as Yup from 'yup';
import Swal from 'sweetalert2';
const { TabPane } = Tabs;
export default function Profile(props) {
    const dispatch = useDispatch();
    const { thongTinTaiKhoan, userLogin } = useSelector(state => state.QuanLyNguoiDungReducer);
    const [ disabled, setDisabled ] = useState(true);
    useEffect(() => {
        dispatch(thongTinTaiKhoanAction());
    }, []);
    const SignupSchema = Yup.object().shape({
        taiKhoan: Yup.string()
            .required('Bạn chưa điền tài khoản'),
        matKhau: Yup.string()
            .required('Bạn chưa điền mật khẩu'),
    });
    const formik = useFormik({
        enableReinitialize: true,
        initialValues: {
            taiKhoan: thongTinTaiKhoan?.taiKhoan,
            matKhau: thongTinTaiKhoan?.matKhau,
            hoTen: thongTinTaiKhoan?.hoTen,
            email: thongTinTaiKhoan?.email,
            soDT: thongTinTaiKhoan?.soDT,
            maNhom: GROUPID,
            maLoaiNguoiDung: userLogin.maLoaiNguoiDung,
        },
        validationSchema: SignupSchema,
        onSubmit: (values) => {
            dispatch(CapNhatNguoiDungAction(values));
        },

    });
    const handleBlur = () => {
        setDisabled(false);
    };
    let disabledClass = disabled ? 'disable-btn cursor-not-allowed' : '';
    return (
        <div style={{ height: 550, margin: "65px", padding: "30px", backgroundColor: 'white', borderRadius: 20 }} className='profile'>
            <Tabs tabPosition={"left"}>
                <TabPane tab={<div style={{ textTransform: 'uppercase', fontSize: 14 }} className='text-gray-600 font-semibold '>
                    Thông tin tài khoản
                </div>} key="1">
                    <div className='flex justify-center items-center'>
                        <form className="w-full max-w-lg" onSubmit={formik.handleSubmit}>
                            <h3 className='text-center'>THÔNG TIN CỦA BẠN</h3>
                            <div className="flex flex-wrap -mx-3 mb-6">
                                <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-first-name">
                                        Họ tên
                                    </label>
                                    <input name='hoTen' onChange={formik.handleChange} onBlur={handleBlur} value={formik.values.hoTen} className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" id="grid-first-name" type="text" placeholder="Jane" />
                                    <p className="text-red-500 text-xs italic">Please fill out this field.</p>
                                </div>
                                <div className="w-full md:w-1/2 px-3">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-last-name">
                                        Email
                                    </label>
                                    <input value={formik.values.email} onBlur={handleBlur} name='email' onChange={formik.handleChange} className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="text" placeholder="Doe@gmail.com" />
                                </div>
                            </div>
                            <div className="flex flex-wrap -mx-3 mb-6">
                                <div className="w-full md:w-1/2 px-3">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-password">
                                        Tài khoản
                                    </label>
                                    <input onBlur={handleBlur} value={formik.values.taiKhoan} name='taiKhoan' onChange={formik.handleChange} disabled='true' className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-password" type="text" placeholder="HelloTix123" />
                                    <p className="text-gray-600 text-xs italic">Make it as long and as crazy as you'd like</p>
                                </div>
                                <div className="w-full md:w-1/2 px-3">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-last-name">
                                        Số điện thoại
                                    </label>
                                    <input onBlur={handleBlur} value={formik.values.soDt} name='soDt' onChange={formik.handleChange} className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="number" placeholder="08659525" />
                                </div>
                            </div>
                            <div className="md:w-2/3 text-center">

                                <button disabled={disabled} className={`shadow bg-yellow-600  focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded ${disabledClass}`} type="submit">
                                    Cập nhật
                                </button>
                            </div>


                        </form>

                    </div>
                </TabPane>
                <TabPane tab={<div style={{ textTransform: 'uppercase', fontSize: 14 }} className='text-gray-600 font-semibold'>
                    Đổi mật khẩu
                </div>} key="2">
                    Content of Tab 2
                </TabPane>

            </Tabs>
        </div>
    );
}
