import { quanLyPhimService } from "../../service/QuanLyPhimService";
import { SET_DANH_SACH_PHIM, SET_THONG_TIN_PHIM } from "../types/QuanLyPhimType";

export const layDanhSachPhimAction = (tenPhim = '') => {
    return async (dispatch) => {
        try {
            const result = await quanLyPhimService.layDanhSachPhim(tenPhim);
            dispatch({
                type: SET_DANH_SACH_PHIM,
                arrFilm: result.data.content
            });
        } catch (error) {

        }
    };
};
export const themPhimUploadHinhAction = (formData) => {
    return async (dispatch) => {
        try {
            let result = await quanLyPhimService.themPhimUploadHinh(formData);
            console.log("file: QuanLyPhimAction.js ~ line 22 ~ return ~ result", result);
            if (result.status === 200) {
                alert('Thêm phim thành công');
            }
        } catch (error) {
            console.log("file: QuanLyPhimAction.js ~ line 24 ~ return ~ error", error);

        }
    };
};

export const LayThongTinPhimAction = (id) => {
    return async (dispatch) => {
        try {
            let result = await quanLyPhimService.LayThongTinPhim(id);
            dispatch({
                type: SET_THONG_TIN_PHIM,
                thongTinPhim: result.data.content
            });
        } catch (error) {
            console.log(error);

        }
    };
};

export const CapNhatThongTinPhimAction = (formData) => {
    return async (dispatch) => {
        try {
            let result = await quanLyPhimService.CapNhatThongTinPhim(formData);
            console.log(result);
        } catch (error) {
            console.log(error);

        }
    };
};

export const XoaPhimAction = (id) => {
    return async (dispatch) => {
        try {
            let result = await quanLyPhimService.XoaPhim(id);
            console.log(result);
            dispatch(layDanhSachPhimAction());
        } catch (error) {
            console.log(error);

        }
    };
};
