import { TOKEN, USER_LOGIN } from "../../util/settings/config";
import { DANG_NHAP_ACTION, EDIT_USER, SET_DANH_SACH_NGUOI_DUNG, SET_THONG_TIN_NGUOI_DUNG } from "../types/QuanLyNguoiDungType";

let user = {};
if (localStorage.getItem(USER_LOGIN)) {
    user = JSON.parse(localStorage.getItem(USER_LOGIN));
}
const initialState = {
    userLogin: user,
    thongTinTaiKhoan: {},
    danhSachNguoiDung: [],
    userEdit: {},
    loaiNguoiDung: [],
    key: "1"
};

export default (state = initialState, action) => {
    switch (action.type) {
        case DANG_NHAP_ACTION: {
            const { thongTinDangnhap } = action;
            localStorage.setItem(USER_LOGIN, JSON.stringify(thongTinDangnhap));
            localStorage.setItem(TOKEN, thongTinDangnhap.accessToken);
            return { ...state, userLogin: thongTinDangnhap };
            break;
        }
        case SET_THONG_TIN_NGUOI_DUNG: {
            return { ...state, thongTinTaiKhoan: action.thongTinTaiKhoan };
            break;
        }
        case SET_DANH_SACH_NGUOI_DUNG: {
            return { ...state, danhSachNguoiDung: action.danhSachNguoiDung };
        }
        case EDIT_USER: {
            return { ...state, userEdit: action.userEdit };
        }
        case 'SET_LOAI_NGUOI_DUNG': {
            return { ...state, loaiNguoiDung: action.loaiNguoiDung };
        }
        case 'CHANGE_KEY': {
            return { ...state, key: action.key };
        }



        default:
            return state;
    }
};
