import React, { Fragment, useEffect, useState } from 'react';
import { Tabs, Radio, Space } from 'antd';
import { NavLink } from 'react-router-dom';
import { history } from '../../../App';
import moment from 'moment';
import { useTranslation } from 'react-i18next';

const { TabPane } = Tabs;

export default function HomeMenu(props) {
    const { heThongRapChieu } = props;
    const { t, i18n } = useTranslation();
    const [ { width, height }, setSize ] = useState({ width: window.innerWidth, height: window.innerHeight });
    useEffect(() => {
        window.onresize = () => {

            const width = window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;
            console.log("file: HomeMenu.js ~ line 17 ~ useEffect ~ width", width);
            const height = window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;
            console.log("file: HomeMenu.js ~ line 18 ~ useEffect ~ height", height);
            setSize({ width, height });
        };
    }, []);

    const renderHeThongRap = () => {
        return heThongRapChieu?.map((heThongRap, index) => {
            return <TabPane tab={<img src={heThongRap.logo} alt={heThongRap.maHeThongRap} className='rounded-full w-16' />} key={index} >
                <Tabs tabPosition={width > 768 ? 'left' : 'top'} style={{ Height: 200 }}>

                    {heThongRap.lstCumRap?.map((cumRap, index) => {

                        return <TabPane tab={
                            <div style={{ width: '300' }} className='flex'>
                                <img src={cumRap.hinhAnh} alt='123' width="50" className='rounded-lg' />
                                <div className='text-left font-semibold ml-2'>
                                    {cumRap.tenCumRap}
                                </div>
                            </div>
                        } key={index} style={{ maxHeight: '550px', overflowY: 'scroll' }}>
                            {cumRap.danhSachPhim?.map((film, index) => {
                                return <Fragment key={index}>
                                    <div className='flex my-5 justify-items-center' >
                                        <div className='flex'>
                                            <img src={film.hinhAnh} alt='123' className='rounded-lg lg:w-32 md:w-14 md:h-7' onError={(e) => { e.target.onerror = null; e.target.src = "/images/movies/love-roise.jpg"; }} />
                                            <div className='ml-4'>
                                                <span className='span-ghe'>C{Math.round((index + 5) / 3)}</span>
                                                <span className='text-2xl	subpixel-antialiased font-semibold	text-black'>-  {film.tenPhim}</span>
                                                <p className='text-green-500 mb-0' style={{ fontSize: 14 }}>{cumRap.diaChi}</p>
                                                <p className='text-gray-400' style={{ fontSize: 12 }}>120 {t('min')} - {(index + 10) / 5} IMDb - TIX 8.6</p>

                                                <h3 className='text-xl'>{t('showtime')}</h3>
                                                <div className='grid lg:grid-cols-5 md:grid-cols-2 sm:grid-cols-1 gap-5'>

                                                    {film.lstLichChieuTheoPhim?.slice(0, 10).map((lichChieu, index) => {

                                                        return <span key='index' className='py-2 px-4 border-gray-800  rounded-lg font-bold text-lichchieu hover:text-yellow-500' style={{ backgroundColor: 'rgb(253, 183, 59)', cursor: 'pointer' }} onClick={() => {
                                                            history.push(`/checkout/${lichChieu.maLichChieu}`);
                                                        }}>
                                                            {moment(lichChieu.ngayChieuGioChieu).format('hh:mm A')}
                                                        </span>;

                                                    })}
                                                </div>

                                            </div>

                                        </div>
                                    </div>

                                </Fragment>;

                            })}
                        </TabPane>;
                    })}
                </Tabs>
            </TabPane>;
        });
    };
    return (


        <Tabs tabPosition={'left'} size='middle' id='menu' className='bg-gray-200 rounded-lg'>
            {renderHeThongRap()}
        </Tabs>

    );
}
