import { history } from "../../App";
import { quanLyNguoiDungService } from "../../service/QuanLyNguoiDungService";
import { DANG_NHAP_ACTION, SET_DANH_SACH_NGUOI_DUNG, SET_THONG_TIN_NGUOI_DUNG } from "../types/QuanLyNguoiDungType";
import Swal from 'sweetalert2';

export const dangNhapAction = (thongtinDangNhap) => {
    return async (dispatch) => {

        try {
            const result = await quanLyNguoiDungService.dangNhap(thongtinDangNhap);
            if (result.status === 200) {
                Swal.fire({
                    title: 'Đăng Nhập!',
                    text: 'Bạn đã đăng nhập thành công!',
                    icon: 'success',
                    confirmButtonText: 'Xác Nhận'
                }).then((info) => {
                    if (info.isConfirmed) {
                        dispatch({
                            type: DANG_NHAP_ACTION,
                            thongTinDangnhap: result.data.content
                        });
                        history.push('/');

                    }
                });
            } else {

            }


        } catch (error) {
            Swal.fire({
                title: 'Đăng Nhập!',
                showDenyButton: true,
                text: 'Tài khoản và mật khẩu không đúng! Vui lòng đăng ký tài khoản mới (nếu cần)',
                icon: 'error',
                confirmButtonText: 'Đăng nhập lại',
                confirmButtonColor: '#FF0000',
                denyButtonText: `Về Trang Chủ`,
                denyButtonColor: '#3085d6'
            }).then((info) => {
                /* Read more about isConfirmed, isDenied below */
                if (info.isDenied) {
                    history.push('/');
                }
            });
        }
    };

};
export const dangKyAction = (user) => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.dangKy(user);
            if (result.status === 200) {
                Swal.fire({
                    title: 'Đăng Ký!',
                    text: 'Bạn đã đăng ký thành công!',
                    icon: 'success',
                    showCancelButton: true,
                    confirmButtonText: 'Đăng nhập',
                    denyButtonText: `Về Trang Chủ`,
                    cancelButtonText: 'Xác nhận'
                }).then((info) => {
                    if (info.isConfirmed) {
                        history.push('/login');
                    }
                    else if (info.isDenied) {
                        history.push('/');
                    }
                });



            }
        } catch (error) {
            Swal.fire({
                title: 'Đăng Ký!',
                text: 'Đã có người có tài khoản này!',
                icon: 'error',
                confirmButtonText: 'Đăng ký lại',
                denyButtonText: `Về Trang Chủ`,
            }).then((info) => {

                if (info.isDenied) {
                    history.push('/');
                }
            });
        }
    };
};
export const dangNhapAdminAction = (thongtinDangNhap) => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.dangNhap(thongtinDangNhap);
            if (result.status === 200) {
                dispatch({
                    type: DANG_NHAP_ACTION,
                    thongTinDangnhap: result.data.content
                });

                history.push('/mainAdmin');
            }
        } catch (error) {
            console.log(error);
        }
    };
};
export const thongTinTaiKhoanAction = () => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.LayThongTinTaiKhoan();
            if (result.status === 200) {
                dispatch({
                    type: SET_THONG_TIN_NGUOI_DUNG,
                    thongTinTaiKhoan: result.data.content
                });
            }
        } catch (error) {
            console.log(error);
        }
    };
};

export const layDanhSachNguoiDungAction = (tukhoa = '') => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.layDanhSachNguoiDung(tukhoa);
            if (result.status === 200) {
                dispatch({
                    type: SET_DANH_SACH_NGUOI_DUNG,
                    danhSachNguoiDung: result.data.content
                });
            }
        } catch (error) {
            console.log(error);
        }
    };
};

export const layDanhSachLoaiNguoiDungAction = () => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.LayDanhSachLoaiNguoiDung();
            if (result.status === 200) {
                dispatch({
                    type: "SET_LOAI_NGUOI_DUNG",
                    loaiNguoiDung: result.data.content
                });
            }
        } catch (error) {
            console.log(error);
        }
    };
};

export const CapNhatNguoiDungAction = (userEdit) => {

    return async (dispatch) => {

        try {

            const result = await quanLyNguoiDungService.CapNhatNguoiDung(userEdit);
            if (result.status === 200) {
                Swal.fire({
                    text: 'Cập nhật thành công!',
                    icon: 'success',
                    showCancelButton: false,
                    confirmButtonText: 'Ok',
                }).then((info) => {

                    if (info.isConfirmed) {

                        history.push('/home');
                        window.location.reload();
                    }
                });;





            }
        } catch (error) {
            console.log(error);
            Swal.fire({
                text: 'Cập nhật thất bại!',
                icon: 'error',
                showCancelButton: false,
                confirmButtonText: 'Ok',
            });
        }
    };
};
export const ThemNguoiDungAction = (user) => {
    return async (dispatch) => {
        try {
            const result = await quanLyNguoiDungService.ThemNguoiDung(user);
            if (result.status === 200) {
                alert('Thêm người dùng thành công!');
            }
        } catch (error) {
            console.log(error);
        }
    };
};
