import React, { Fragment } from 'react';
import ModalVideo from 'react-modal-video';
import { useDispatch, useSelector } from 'react-redux';
export default function VideoModal() {
    const { isOpen, video } = useSelector(state => state.ModalVideoReducer);
    const dispatch = useDispatch();
    return (
        <Fragment>
            <ModalVideo channel='youtube' autoplay isOpen={isOpen} url='https://www.youtube.com/watch?v=oCl5xx2alMk' onClose={() => {
                dispatch({
                    type: 'CLOSE_VIDEO'
                });
            }} />
        </Fragment>
    );
}
