import React from 'react';
import './LoginAdmin.css';
import { useFormik } from 'formik';
import { useDispatch, useSelector } from 'react-redux';
import { history } from '../../App';
export default function LoginAdmin(props) {
    const dispatch = useDispatch();
    const formik = useFormik({
        initialValues: {
            taiKhoan: '',
            matKhau: ''
        },
        onSubmit: values => {

        },
    });
    return (
        <div className="bg-gray-300" style={{ fontFamily: 'Roboto' }}>
            <div className="h-screen flex justify-center items-center">
                <div className="bg-white rounded-lg w-2/5 px-16 py-16">
                    <form onSubmit={formik.handleSubmit}>
                        <div className="flex font-bold justify-center">
                            <img className="h-20 w-20" src="https://raw.githubusercontent.com/sefyudem/Responsive-Login-Form/master/img/avatar.svg" />
                        </div>
                        <h2 className="text-3xl text-center text-gray-700 mb-4">Admin Login Form</h2>
                        <div className="input-div border-b-2 relative grid my-5 py-1 focus:outline-none" style={{ gridTemplateColumns: '7% 93%' }}>
                            <div className="i">
                                <i className="fas fa-user" />
                            </div>
                            <div className="div">

                                <input type="text" className="absolute w-full h-full py-2 px-3 outline-none inset-0 text-gray-700" style={{ background: 'none' }} placeholder='Tài khoản' />
                            </div>
                        </div>
                        <div className="input-div border-b-2 relative grid my-5 py-1 focus:outline-none" style={{ gridTemplateColumns: '7% 93%' }}>
                            <div className="i">
                                <i className="fas fa-lock" />
                            </div>
                            <div className="div">

                                <input type="password" className="absolute w-full h-full py-2 px-3 outline-none inset-0 text-gray-700" style={{ background: 'none' }} placeholder='Mật khẩu...' />
                            </div>
                        </div>
                        <a href="#" className="text-xs text-green-400 hover:text-green-500 float-right mb-4">Forgot Password?</a>
                        <button type="submit" className="w-full py-2 rounded-full bg-green-600 text-gray-100  focus:outline-none">Đăng nhập</button>
                    </form>
                </div>
            </div>
        </div>

    );
}
