import React, { Fragment, useEffect } from 'react';
import { Table, Input } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { layDanhSachLoaiNguoiDungAction, layDanhSachNguoiDungAction } from '../../../redux/actions/QuanLyNguoiDungAction';
import { EditTwoTone, DeleteTwoTone, CalendarOutlined, DeleteOutlined } from '@ant-design/icons';
import { NavLink } from 'react-router-dom';
import { EDIT_USER } from '../../../redux/types/QuanLyNguoiDungType';
const { Search } = Input;


export default function Dashboard() {
    const { danhSachNguoiDung } = useSelector(state => state.QuanLyNguoiDungReducer);
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(layDanhSachNguoiDungAction());
    }, []);
    const columns = [
        {
            title: 'STT',
            dataIndex: 'stt',
            render: (text, user, index) => {
                return <div>
                    {index + 1}
                </div>;
            },
            // specify the condition of filtering result
            // here is that finding the name started with `value`

        },
        {
            title: 'Tài Khoản',
            dataIndex: 'taiKhoan',
            defaultSortOrder: 'descend',
            sorter: (a, b) => a.taiKhoan.length - b.taiKhoan.length,
        },
        {
            title: 'Họ Tên',
            dataIndex: 'hoTen',
            sorter: (a, b) => a.hoTen.length - b.hoTen.length,
        },
        {
            title: 'Email',
            dataIndex: 'email',
            sorter: (a, b) => a.email.length - b.email.length,
        },
        {
            title: 'Phone',
            dataIndex: 'soDt',
            sorter: (a, b) => a.soDt.length - b.soDt.length,
        },
        {
            title: 'Loại người dùng',
            dataIndex: 'maLoaiNguoiDung',
            filters: [
                {
                    text: 'Khách Hàng',
                    value: 'KhachHang',
                },
                {
                    text: 'Quản trị',
                    value: 'QuanTri',
                },
            ],
            onFilter: (value, record) => record.maLoaiNguoiDung.indexOf(value) === 0,
        },
        {
            title: 'Hành động',
            dataIndex: 'hanhDong',
            render: (text, user, index) => {
                return <Fragment>
                    <NavLink key='1' className='text-2xl mr-2' to={`/mainAdmin/users/edituser/${user.hoTen}`} onClick={() => {
                        dispatch({
                            type: EDIT_USER,
                            userEdit: user
                        });
                    }}><EditTwoTone style={{ color: 'blue' }} /></NavLink>
                    <span key='2' style={{ cursor: 'pointer' }} to="/" className='text-2xl' onClick={() => {
                        if (window.confirm('Bạn có chắc chắc muốn xóa người này')) {

                        }
                    }}><DeleteOutlined style={{ color: 'red' }} /></span>
                </Fragment>;
            },

        },
    ];

    function onChange(pagination, filters, sorter, extra) {
        console.log('params', pagination, filters, sorter, extra);
    }
    const onSearch = (value) => {
        dispatch(layDanhSachNguoiDungAction(value));
    };
    return (
        <div>
            <h3>User</h3>
            <Search
                className='my-5'
                placeholder="input search text"
                allowClear
                enterButton="Search"
                size="large"
                onSearch={onSearch}
            />
            <Table columns={columns} dataSource={danhSachNguoiDung} onChange={onChange} />
        </div>
    );
}
