import React, { useEffect } from 'react';
import { Carousel } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import { getCarouselAction } from '../../../../redux/actions/CarouselAction';
import OwlCarousel from 'react-owl-carousel2';
import 'react-owl-carousel2/lib/styles.css';
import 'react-owl-carousel2/src/owl.theme.default.css';
import { layDanhSachPhimAction } from '../../../../redux/actions/QuanLyPhimAction';

const dataCarousel = [
    {
        "maPhim": 1352,
        "tenPhim": "Bloodshot 1",
        "biDanh": "bloodshot-1",
        "trailer": "https://www.youtube.com/embed/vOUVVDWdXbo",
        "hinhAnh": "./images/bat-man.png",
        "moTa": "Siêu anh hùng Bloodshot là bộ phim được định hình mở màn cho loạt phim lấy bối cảnh trong vũ trụ điện ảnh Valiant Comics. Nhân vật chính của phim là tài tử Vin Diesel, với đạo diễn  David SF Wilson kết hợp cùng kịch bản của Jeff Wadlow và Eric Heisserer. Phim xoay quanh câu chuyện của một thuỷ quân lục chiến đã bị giết trong lúc hành động, tuy nhiên anh đã được hồi sinh với siêu năng lực bởi một tổ chức ngầm muốn dùng anh như một vũ khí để loại trừ các đối thủ.",
        "maNhom": "GP09",
        "ngayKhoiChieu": "2021-09-18T16:09:43.097",
        "danhGia": 10,
        "hot": true,
        "dangChieu": false,
        "sapChieu": true,
        time: 120
    },
    {
        "maPhim": 1426,
        "tenPhim": "Fantastic Four",
        "biDanh": "fantastic-four",
        "trailer": "https://www.youtube.com/embed/AAgnQdiZFsQ",
        "hinhAnh": "http://movieapi.cyberlearn.vn/hinhanh/fantasticfour.jpg",
        "moTa": "Four young outsiders teleport to an alternate and dangerous universe which alters their physical form in shocking ways. The four must learn to harness their new abilities and work together to save Earth from a former friend turned enemy.",
        "maNhom": "GP08",
        "ngayKhoiChieu": "2019-07-29T00:00:00",
        "danhGia": 5,
        "hot": true,
        "dangChieu": false,
        "sapChieu": true,
        "time": 160

    },
    {
        "maPhim": 1381,
        "tenPhim": "Batman v Superman: Dawn of Justice",
        "biDanh": "batman-v-superman-dawn-of-justice",
        "trailer": "https://www.youtube.com/embed/0WWzgGyAH6Y",
        "hinhAnh": "http://movieapi.cyberlearn.vn/hinhanh/batmanvssuperman.jpg",
        "moTa": "Fearing the actions of a god-like Super Hero left unchecked, Gotham City's own formidable, forceful vigilante takes on Metropolis most revered, modern-day savior, while the world wrestles with what sort of hero it really needs. And with Batman and Superman at war with one another, a new threat quickly arises, putting mankind in greater danger than it's ever known before.",
        "maNhom": "GP08",
        "ngayKhoiChieu": "2019-07-29T00:00:00",
        "danhGia": 5,
        "hot": false,
        "dangChieu": true,
        "sapChieu": false,
        "time": 150
    },

];
export default function HomeCarousel() {
    const { arrDefault } = useSelector(state => state.QuanLyPhimReducer);
    console.log("file: HomeCarousel.js ~ line 21 ~ HomeCarousel ~ arrDefault", arrDefault);
    const dispatch = useDispatch();
    //tự kích hoạt khi component load ra
    useEffect(() => {
        dispatch(layDanhSachPhimAction());
    }, []);
    const renderImg = () => {
        return dataCarousel.map((film, index) => {
            return <div className="hero-slide-item">
                <img src={film.hinhAnh} alt />
                <div className="overlay" />
                <div className="hero-slide-item-content">
                    <div className="item-content-wraper">
                        <div className="item-content-title top-down">
                            {film.tenPhim}
                        </div>
                        <div className="movie-infos top-down delay-2">
                            <div className="movie-info">
                                <i className="bx bxs-star" />
                                <span>{film.danhGia}</span>
                            </div>
                            <div className="movie-info">
                                <i className="bx bxs-time" />
                                <span>{film.time} mins</span>
                            </div>
                            <div className="movie-info">
                                <span>HD</span>
                            </div>
                            <div className="movie-info">
                                <span>16+</span>
                            </div>
                        </div>
                        <div className="item-content-description top-down delay-4">
                            {film.moTa}
                        </div>
                        <div className="item-action top-down delay-6">
                            <a href="#" className="btn btn-hover">
                                <i className="bx bxs-right-arrow" />
                                <span>Watch now</span>
                            </a>
                        </div>
                    </div>
                </div>
            </div>;
        });
    };
    let navText = [ "<i class='bx bx-chevron-left'></i>", "<i class='bx bx-chevron-right'></i>" ];
    const options = {
        items: 1,
        dots: false,
        loop: true,
        nav: true,
        navText: navText,
        autoplay: true,
        autoplayHoverPause: true
    };
    return (
        <div className="hero-section"  >
            {/* HERO SLIDE */}
            <div className="hero-slide">
                <OwlCarousel className="owl-carousel carousel-nav-center" options={options} id="hero-carousel">
                    {renderImg()}
                </OwlCarousel>
            </div>

        </div>

        // <Carousel effect="fade">
        //     {renderImg()}
        // </Carousel>
    );
}
