    const { film } = props;

    return (
        <div div className="wrapper" >
            <div className="card">
                <img src={film.hinhAnh} alt={film.tenPhim} onError={(e) => {
                    let film = document.getElementsByClassName('wrapper');
                    film.setAttribute("style", "display:none;");
                }} />

                <div className="descriptions">
                    <h1>{film.tenPhim}</h1>
                    {film.moTa.length > 100 ? <p>{film.moTa.slice(0, 100)}...</p> : <p>{film.moTa}</p>}

                    {/* <button>
                        <i className="fab fa-youtube" />
                        Play trailer on YouTube
