                        <button className='h-10 text-red-100 transition-colors duration-150 bg-red-700 rounded-lg focus:shadow-outline hover:bg-red-800 w-full' onClick={() => {
                            history.push(`/details/${film.maPhim}`);
                        }}>
                            Đặt Vé
                        </button>
                    </div>

                </div>
            </div>
        </div >




