import React from 'react';
import '../Login/Login.css';
import './Register.css';
import { history } from '../../App';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { GROUPID } from '../../util/settings/config';
import { useDispatch } from 'react-redux';
import { dangKyAction } from '../../redux/actions/QuanLyNguoiDungAction';
export default function Register(props) {
    const dispatch = useDispatch();
    const SignupSchema = Yup.object().shape({
        taiKhoan: Yup.string()
            .required('Bạn chưa điền tài khoản').min(2, 'Kí tự tài khoản quá ngắn!')
            .max(50, 'Kí tự tài khoản quá dài!'),
        matKhau: Yup.string()
            .required('Bạn chưa điền mật khẩu').min(5, 'Kí tự mật khẩu quá ngắn!')
            .max(50, 'Kí tự mật khẩu quá dài!'),
        email: Yup.string()
            .required('Bạn chưa điền email').email('Email không dúng!'),
        soDt: Yup.string().required('Bạn chưa điền số điện thoại').matches(
            /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/,
            "Số điện thoại không đúng"
        ),
        hoTen: Yup.string()
            .required('Bạn chưa điền họ tên').min(2, 'Tên quá ngắn!')
            .max(50, 'Tên quá dài!'),
        xacNhanMatKhau: Yup.string()
            .oneOf([ Yup.ref('matKhau'), null ], 'Mật khẩu xác nhận không đúng!')
    });
    const formik = useFormik({
        initialValues: {
            taiKhoan: '',
            matKhau: '',
            email: '',
            soDt: '',
            maNhom: GROUPID,
            hoTen: '',
            xacNhanMatKhau: ''
        },
        validationSchema: SignupSchema,
        onSubmit: values => {
            dispatch(dangKyAction(values));

        },
    });
    return (
        <div className='register-wrapper'>

            <img className="signin--header ml-auto mr-auto" src="https://tix.vn/app/assets/img/login/group@2x.png" />


            <form className='font-sans' onSubmitCapture={formik.handleSubmit}>
                <h2 class="text-center text-4xl text-blue-300 font-display font-semibold  xl:text-2xl
                    xl:text-bold">Đăng Ký</h2>
                <div className='grid grid-cols-2 gap-3'>
                    <div className="mb-4">
                        <label className="block text-white text-sm font-display font-semibold mb-2 text-left" htmlFor="username">
                            Email
                        </label>
                        <input onChange={formik.handleChange} name='email' className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline  ${formik.errors.email ? 'border-red-500' : ''}`} id="username" type="text" placeholder="abc@gmail.com...." />
                        <p className="text-red-500 text-xs italic mb-0">{formik.errors.email}</p>
                    </div>
                    <div className="mb-4">
                        <label className="block text-white text-sm font-display font-semibold mb-2 text-left" htmlFor="username">
                            Số điện thoại
                        </label>
                        <input onChange={formik.handleChange} name='soDt' className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" ${formik.errors.soDt ? 'border-red-500' : ''}`} type="text" placeholder="+84............" />
                        <p className="text-red-500 text-xs italic mb-0">{formik.errors.soDt}</p>
                    </div>
                    <div className="mb-4">
                        <label className="block text-white text-sm font-display font-semibold mb-2 text-left" htmlFor="username">
                            Họ tên
                        </label>
                        <input onChange={formik.handleChange} name='hoTen' className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${formik.errors.hoTen ? 'border-red-500' : ''}`} id="username" type="text" placeholder="Nguyễn Văn A....." />
                        <p className="text-red-500 text-xs italic mb-0">{formik.errors.hoTen}</p>
                    </div>
                    <div className="mb-4">
                        <label className="block text-white text-sm font-display font-semibold mb-2 text-left" htmlFor="username">
                            Tài Khoản
                        </label>
                        <input onChange={formik.handleChange} name='taiKhoan' className={`shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline ${formik.errors.taiKhoan ? 'border-red-500' : ''}`} id="username" type="text" placeholder="Username" />
                        <p className="text-red-500 text-xs italic mb-0">{formik.errors.taiKhoan}</p>
                    </div>
                    <div className="mb-2">
                        <label className="block  text-sm font-display font-semibold mb-2 text-left text-white" htmlFor="password">
                            Mật Khẩu
                        </label>
                        <input onChange={formik.handleChange} name='matKhau' className={`shadow appearance-none border ${formik.errors.matKhau ? 'border-red-500' : ''} rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline`} id="password" type="password" placeholder="******************" />
                        <p className="text-red-500 text-xs italic mb-0">{formik.errors.matKhau}</p>
                    </div>
                    <div className="mb-2">
                        <label className="block  text-sm font-display font-semibold mb-2 text-left text-white" htmlFor="password">
                            Xác nhận mật khẩu
                        </label>
                        <input onChange={formik.handleChange} name='xacNhanMatKhau' className={`shadow appearance-none border ${formik.errors.xacNhanMatKhau ? 'border-red-500' : ''} rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline`} type="password" placeholder="******************" />
                        <p className="text-red-500 text-xs italic mb-0">{formik.errors.xacNhanMatKhau}</p>
                    </div>

                    <div class="text-sm font-display font-semibold text-white text-center mb-5 col-span-2">
                        Bạn đã có tài khoản ? <a class="cursor-pointer text-indigo-600 hover:text-indigo-800" onClick={() => {
                            history.push('/login');
                        }}>Đăng Nhập</a>
                    </div>
                </div>
                <button class="bg-blue-500 hover:bg-blue-700 text-white font-display font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline mb-5" type="submit">
                    Đăng ký
                </button>
            </form>
            <div className="signin-close" onClick={() => {
                history.push('/');
            }} />
        </div>
    );
}
