import React, { Fragment } from 'react';
import ReactPlayer from 'react-player';
import { Modal, Button } from 'antd';

import { useDispatch, useSelector } from 'react-redux';
export default function VideoModal() {
    const { isOpen, video } = useSelector(state => state.ModalVideoReducer);
    const dispatch = useDispatch();
    return (
        <Fragment>
            <Modal
                centered
                visible={isOpen}
                style={{ width: "100%" }}
                onOk={() => { dispatch({ type: "CLOSE_VIDEO" }); }}
                onCancel={() => { dispatch({ type: "CLOSE_VIDEO" }, ReactPlayer.removeCustomPlayers()); }}
                width={1000}
            >
                <ReactPlayer url={video} playIcon controls stopOnUnmount pip={isOpen} />
            </Modal>
        </Fragment>
    );
}
