import React, { useEffect, useState } from 'react';
import HomeMenu from './HomeMenu/HomeMenu';
import { useSelector, useDispatch } from 'react-redux';
import Film from '../../components/Film/Film';
import MutipleRowSlick from '../../components/Rslick/MutipleRowSlick';
import { layDanhSachPhimAction } from '../../redux/actions/QuanLyPhimAction';
import _ from 'lodash';
import { history } from '../../App';
import { layDanhSachHeThongRapAction } from '../../redux/actions/QuanLyRapAction';
import HomeCarousel from '../../templates/HomeTemplate/Layout/HomeCarousel/HomeCarousel';
import OwlCarousel from 'react-owl-carousel2';
import 'react-owl-carousel2/lib/styles.css';
import { DownOutlined } from '@ant-design/icons';
import { useTranslation } from 'react-i18next';

import 'react-owl-carousel2/src/owl.theme.default.css';
import Mobile from '../Mobile/Mobile';
import { Tabs } from 'antd';


const { TabPane } = Tabs;
export default function Home(props) {
    const { arrFilm } = useSelector(state => state.QuanLyPhimReducer);
    const { t, i18n } = useTranslation();
    const { heThongRapChieu } = useSelector(state => state.QuanLyRapReducer);
    let [ add, setAdd ] = useState(12);
    const arrFilmUniq = _.uniqBy(arrFilm, 'tenPhim');
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(layDanhSachPhimAction());
        dispatch(layDanhSachHeThongRapAction());
    }, []);
    let navText = [ "<i class='bx bx-chevron-left'></i>", "<i class='bx bx-chevron-right'></i>" ];
    const options = {
        items: 2,
        dots: false,
        nav: true,
        loop: true,
        navText: navText,
        margin: 15,
        responsive: {
            500: {
                items: 2
            },
            1280: {
                items: 4
            },
            1600: {
                items: 6
            }
        }
    };
    const renderFilms = () => {
        return arrFilmUniq.slice(25, 60).map((film, index) => {
            return <a className="movie-item">
                <img src={film.hinhAnh} alt={film.hinhAnh} onError={(e) => {
                    e.target.src = 'images/movies/transformer.jpg';
                }} />
                <div className="movie-item-content text-white">
                    <div className="movie-item-title">
                        {film.tenPhim}
                    </div>
                    <div className="movie-infos">
                        <div className="movie-info">
                            <i className="bx bxs-star" />
                            <span>{film.danhGia}</span>
                        </div>
                        <div className="movie-info">
                            <i className="bx bxs-time" />
                            <span>120 {t('min')}</span>
                        </div>
                        <div className="movie-info">
                            <span>HD</span>
                        </div>
                        <div className="movie-info">
                            <span>16+</span>
                        </div>
                    </div>
                    <div className='mt-4 flex justify-between movie-action hidden'>
                        <button className='bg-transparent border-2 border-white rounded-full hover:bg-red-700 hover:text-white text-red-500 font-bold py-2 px-4 rounded mr-2' onClick={() => {
                            dispatch({
                                type: 'OPEN_VIDEO',
                                video: film.trailer
                            });
                        }}><i className='bx bxs-right-arrow'></i> {t('trailer')}</button>
                        <button className='bg-transparent border-2 border-white rounded-full hover:bg-green-700 hover:text-white text-green-500 font-bold py-2 px-4 rounded' onClick={() => {
                            history.push(`/details/${film.maPhim}`);
                        }}><i className='bx bxs-movie'></i> {t('booking')}</button>
                    </div>
                </div>
            </a>;
        });
    };
    const renderShowingMovie = () => {
        return arrFilmUniq.filter(film => film.dangChieu === true).slice(8, add).map((films, index) => {
            return <a href="#" className="movie-item ">
                <img src={films.hinhAnh} alt onError={(e) => {
                    e.target.src = './images/movies/captain-marvel.png';
                }} />
                <div className="movie-item-content">
                    <div className="movie-item-title">
                        {films.tenPhim}
                    </div>
                    <div className="movie-infos">
                        <div className="movie-info">
                            <i className="bx bxs-star" />
                            <span>{films.danhGia}</span>
                        </div>
                        <div className="movie-info">
                            <i className="bx bxs-time" />
                            <span>120 {t('min')}</span>
                        </div>
                        <div className="movie-info">
                            <span>HD</span>
                        </div>
                        <div className="movie-info">
                            <span>16+</span>
                        </div>
                        <div className='mt-4 flex justify-between movie-action hidden'>
                            <button className='bg-transparent border-2 border-white rounded-full hover:bg-red-700 hover:text-white text-red-500 font-bold py-2 px-4 rounded mr-2' onClick={() => {
                                dispatch({
                                    type: 'OPEN_VIDEO',
                                    video: films.trailer
                                });
                            }}><i className='bx bxs-right-arrow'></i> {t('trailer')}</button>
                            <button className='bg-transparent border-2 border-white rounded-full hover:bg-green-700 hover:text-white text-green-500 font-bold py-2 px-4 rounded' onClick={() => {
                                history.push(`/details/${films.maPhim}`);
                            }}><i className='bx bxs-movie'></i> {t('booking')}</button>
                        </div>
                    </div>
                </div>
            </a>;
        });
    };
    const renderUpcomingMovie = () => {
        return arrFilmUniq.filter(film => film.sapChieu === true).slice(8, add).map((films, index) => {
            return <a href="#" className="movie-item ">
                <img src={films.hinhAnh} alt onError={(e) => {
                    e.target.src = './images/movies/captain-marvel.png';
                }} />
                <div className="movie-item-content">
                    <div className="movie-item-title">
                        {films.tenPhim}
                    </div>
                    <div className="movie-infos">
                        <div className="movie-info">
                            <i className="bx bxs-star" />
                            <span>{films.danhGia}</span>
                        </div>
                        <div className="movie-info">
                            <i className="bx bxs-time" />
                            <span>120 {t('min')}</span>
                        </div>
                        <div className="movie-info">
                            <span>HD</span>
                        </div>
                        <div className="movie-info">
                            <span>16+</span>
                        </div>
                        <div className='mt-4 flex justify-between movie-action hidden'>
                            <button className='bg-transparent border-2 border-white rounded-full hover:bg-red-700 hover:text-white text-red-500 font-bold py-2 px-4 rounded mr-2' onClick={() => {
                                dispatch({
                                    type: 'OPEN_VIDEO',
                                    video: films.trailer
                                });
                            }}><i className='bx bxs-right-arrow'></i> {t('trailer')}</button>
                            <button className='bg-transparent border-2 border-white rounded-full hover:bg-green-700 hover:text-white text-green-500 font-bold py-2 px-4 rounded' onClick={() => {
                                history.push(`/details/${films.maPhim}`);
                            }}><i className='bx bxs-movie'></i> {t('booking')}</button>
                        </div>
                    </div>
                </div>
            </a>;
        });
    };
    return (
        <div>

            <HomeCarousel />
            <div className="section animate__animated animate__fadeInUp" id='movie_hot'>
                <div className="container">
                    <div className="section-header text-white animate__animated animate__rubberBand">
                        {t('moviehot')}
                    </div>
                    <OwlCarousel className="movies-slide carousel-nav-center owl-carousel" options={options}>
                        {/* MOVIE ITEM */}
                        {renderFilms()}
                    </OwlCarousel>
                </div>
            </div>

            <Tabs defaultActiveKey="1" centered style={{ padding: 40 }}>
                <TabPane tab={<div className="section-header animate__animated animate__tada text-white" style={{ borderLeft: 'none', marginBottom: 0, paddingLeft: 0 }} >
                    {t('movieshowing')}
                </div>} key="1">
                    <div className="movies-slide animate__animated animate__fadeInRightBig carousel-nav-center grid sm:grid-cols-2 md:grid-cols-4 gap-5 text-white">
                        {/* MOVIE ITEM */}
                        {renderShowingMovie()}
                    </div>
                    <div className='text-center '>
                        <button className='button-addmore' onClick={() => {
                            setAdd(add += 4);
                        }}>{t('addmore')} <DownOutlined /></button>
                    </div>

                </TabPane>
                <TabPane tab={<div className="section-header animate__animated animate__tada text-white" style={{ marginBottom: 0 }}>
                    {t('moviecoming')}
                </div>} key="2">
                    <div className="movies-slide animate__animated animate__fadeInLeftBig carousel-nav-center grid sm:grid-cols-2 md:grid-cols-4 gap-5 text-white">
                        {/* MOVIE ITEM */}
                        {renderUpcomingMovie()}
                    </div>
                    <div className='text-center '>
                        <button className='button-addmore' onClick={() => {
                            setAdd(add += 4);
                        }}>  {t('addmore')} <DownOutlined /></button>
                    </div>
                </TabPane>
            </Tabs>

            <div className='container mx-auto ' style={{ marginTop: 70 }} id='theater'>


                <div className="section-header text-white">
                    {t('theater')}
                </div>
                <HomeMenu heThongRapChieu={heThongRapChieu} />
            </div>
            <Mobile />
        </div>
    );
}
