import { DAT_GHE, SET_CHI_TIET_PHONG_VE } from "../types/QuanLyDatVeType";

const initialState = {
    chiTietPhongVe: {},
    danhSachGheDangDat: [
        {
            daDat: false,
            loaiGhe: "Thuong",
            maGhe: 50761,
            maRap: 472,
            stt: "01",
            taiKhoanNguoiDat: null,
            tenGhe: "01"
        }
    ]
};


export default (state = initialState, action) => {
    switch (action.type) {
        case SET_CHI_TIET_PHONG_VE: {
            return { ...state, chiTietPhongVe: action.chiTietPhongVe };
        }
        case DAT_GHE: {
            //cập nhật danh sách ghế đang đặt
            let index = state.danhSachGheDangDat.findIndex(gheDD => gheDD.maGhe === action.gheDuocChon.maGhe);
            if (index != -1) {
                state.danhSachGheDangDat.splice(index, 1);
            } else {
                state.danhSachGheDangDat.push(action.gheDuocChon);
            }
            return { ...state };
        }

        default:
            return state;
    }
};
