    };
    return (
        <div>
            <h3>User</h3>
            <Search
                className='my-5'
                placeholder="input search text"
                allowClear
                enterButton="Search"
                size="large"
                onSearch={onSearch}
            />
            <Table columns={columns} dataSource={danhSachNguoiDung} onChange={onChange} />
        </div>
    );
}
