import React, { useEffect, useState } from 'react';
import {
    Form,
    Input,
    Button,
    Radio,
    Select,
    Cascader,
    DatePicker,
    InputNumber,
    TreeSelect,
    Switch,
} from 'antd';
import { useFormik } from 'formik';
import moment from 'moment';
import { useDispatch, useSelector } from 'react-redux';
import { CapNhatThongTinPhimAction, LayThongTinPhimAction, themPhimUploadHinhAction } from '../../../redux/actions/QuanLyPhimAction';
import { GROUPID } from '../../../util/settings/config';
import { CapNhatNguoiDungAction, layDanhSachLoaiNguoiDungAction, ThemNguoiDungAction } from '../../../redux/actions/QuanLyNguoiDungAction';
export default function EditUser(props) {
    const [ componentSize, setComponentSize ] = useState('default');
    const { userEdit, loaiNguoiDung } = useSelector(state => state.QuanLyNguoiDungReducer);
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(layDanhSachLoaiNguoiDungAction());
    }, []);
    const onFormLayoutChange = ({ size }) => {
        setComponentSize(size);
    };
    const formik = useFormik({
        enableReinitialize: true,
        initialValues: {
            taiKhoan: userEdit.taiKhoan,
            matKhau: userEdit.matKhau,
            email: userEdit.email,
            soDt: userEdit.soDt,
            maNhom: GROUPID,
            maLoaiNguoiDung: userEdit.maLoaiNguoiDung,
            hoTen: userEdit.hoTen


        },
        onSubmit: (values) => {
            console.log(values);
            dispatch(CapNhatNguoiDungAction(values));
        },
    });
    const handleChangeSelect = (values) => {
        formik.setFieldValue('maLoaiNguoiDung', values);
    };
    const handleChangeAddUser = () => {
        console.log(formik.values);
        if (window.confirm('Bạn muốn thêm người này')) {
            dispatch(ThemNguoiDungAction(formik.values));
        }
    };


    return (
        <Form

            labelCol={{
                span: 4,
            }}
            wrapperCol={{
                span: 14,
            }}
            layout="horizontal"
            initialValues={{
                size: componentSize,
            }}
            onValuesChange={onFormLayoutChange}
            size={componentSize}
        >
            <Form.Item label="Form Size" name="size">
                <Radio.Group>
                    <Radio.Button value="small">Small</Radio.Button>
                    <Radio.Button value="default">Default</Radio.Button>
                    <Radio.Button value="large">Large</Radio.Button>
                </Radio.Group>
            </Form.Item>
            <div className='grid grid-cols-2 gap-3'>
                <Form.Item label="Tài Khoản" >
                    <Input name="taiKhoan" onChange={formik.handleChange} value={formik.values.taiKhoan} />
                </Form.Item>
                <Form.Item label="Email">
                    <Input name="email" onChange={formik.handleChange} value={formik.values.email} />
                </Form.Item>
                <Form.Item label="Mật khẩu">
                    <Input name="matKhau" onChange={formik.handleChange} value={formik.values.matKhau} />
                </Form.Item>
                <Form.Item label="Số điện thoại">
                    <Input name="soDt" onChange={formik.handleChange} type='tel' value={formik.values.soDt} />
                </Form.Item>
                <Form.Item label="Họ tên">
                    <Input name="hoTen" onChange={formik.handleChange} value={formik.values.hoTen} />
                </Form.Item>
                <Form.Item label='Loại người'>
                    <Select placeholder="Please select" onChange={handleChangeSelect} options={loaiNguoiDung?.map((loainguoi, index) => ({ label: loainguoi.tenLoai, value: loainguoi.maLoaiNguoiDung }))} value={formik.values.maLoaiNguoiDung} />
                </Form.Item>

            </div>
            <div className='flex justify-around'>
                <Form.Item >
                    <button type="submit" onClick={formik.handleSubmit} className='bg-green-700 text-white p-5 rounded-full'>Sửa</button>
                </Form.Item>
                <Form.Item >
                    <button onClick={handleChangeAddUser} className='bg-blue-700 text-white p-5 rounded-full'>Thêm</button>
                </Form.Item>

            </div>




        </Form>
    );
}
