import React from 'react';
import { useSelector } from 'react-redux';

import _ from 'lodash';
export default function Footer(props) {
    const { heThongRapChieu } = useSelector(state => state.QuanLyRapReducer);
    const arrHeThongRap = _.map(heThongRapChieu, (heThongRap) => _.pick(heThongRap, [ 'maHeThongRap', 'tenHeThongRap', 'logo' ]));
    return (
        <footer className='footer-movie'>
            <div className='footer-content px-20'>
                <div className='footer-top grid grid-cols-4 gap-5'>
                    <div className='footer-tix'>
                        <a href="#"><h3>TIX</h3></a>
                        <a href="#"><p>FAQ</p></a>
                        <a href="#"><p>Điều khoản TIX</p></a>
                        <a href="#"><p>Thỏa thuận sử dụng</p></a>
                        <a href="#"><p>Chính sách bảo mật</p></a>
                        <a href="#"><p>Giấy phép bản quyền</p></a>
                    </div>
                    <div className='footer-rap'>
                        <h3>Đối tác</h3>
                        <div className='footer-rap-img grid grid-cols-4 text-center'>
                            {arrHeThongRap.map((rap, index) => {
                                console.log(rap);
                                return <img src={rap.logo} alt='123' style={{ width: 40, height: 40 }} />;

                            })}
                            <img src="https://tix.vn/app/assets/img/icons/cnx.jpg" className='rounded-full' alt='123' style={{ width: 40, height: 40 }} />
                            <img src="https://tix.vn/app/assets/img/icons/TOUCH.png" className='rounded-full' alt='123' style={{ width: 40, height: 40 }} />
                            <img src="https://tix.vn/app/assets/img/icons/dongdacinema.png" className='rounded-full' alt='123' style={{ width: 40, height: 40 }} />
                            <img src="https://tix.vn/app/assets/img/icons/cnx.jpg" className='rounded-full' alt='123' style={{ width: 40, height: 40 }} />
                            <img src="https://tix.vn/app/assets/img/icons/cnx.jpg" className='rounded-full' alt='123' style={{ width: 40, height: 40 }} />
                            <img src="https://tix.vn/app/assets/img/icons/cnx.jpg" className='rounded-full' alt='123' style={{ width: 40, height: 40 }} />
                            <img src="https://tix.vn/app/assets/img/icons/cnx.jpg" className='rounded-full' alt='123' style={{ width: 40, height: 40 }} />
                        </div>
                    </div>
                </div>
            </div>
        </footer>

    );
}
