import { GROUPID } from "../util/settings/config";
import { baseService } from "./baseService";


class QuanLyRapService extends baseService {
    constructor() {
        super();
    }
    layDanhSachBanner = () => {
        return this.get(`/api/QuanLyRap/LayThongTinHeThongRap`);
    };
    layThongTinLichChieuHeThongRap = () => {
        return this.get(`/api/QuanLyRap/LayThongTinLichChieuHeThongRap?${GROUPID}`);
    };
    layThongTinLichChieuPhim = (id) => {
        return this.get(`/api/QuanLyRap/LayThongTinLichChieuPhim?MaPhim=${id}`);
    };
}

export const quanLyRapService = new QuanLyRapService();
