import React, { Component } from 'react';
import Slider from "react-slick";
import { LeftOutlined, RightOutlined } from '@ant-design/icons';
import styleSlick from './MultipleRowSlick.module.css';
import Film from '../Film/Film';
import { useDispatch, useSelector } from 'react-redux';
import { SET_FILM_DANG_CHIEU, SET_FILM_SAP_CHIEU } from '../../redux/types/QuanLyPhimType';
function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={`${className} ${styleSlick[ 'slick-prev' ]}`}
      style={{ ...style, display: "block" }}
      onClick={onClick}
    >
    </div>

  );
}
function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={`${className} ${styleSlick[ 'slick-prev' ]}`}

      style={{ ...style, display: "block", left: '-50px' }}
      onClick={onClick}
    >
    </div>
  );
}
const MultipleRowSlick = (props) => {
  const dispatch = useDispatch();
  const { dangChieu, sapChieu } = useSelector(state => state.QuanLyPhimReducer);
  const renderFilms = () => {
    return props.arrFilm.map((item, index) => {
      return <div className={`${styleSlick[ 'width-item' ]}`} key={index}  >
        <Film film={item} />
      </div>;
    });
  };
  const settings = {
    className: "center variable-width",
    centerMode: true,
    infinite: true,
    centerPadding: "5px",
    slidesToShow: 2,
    speed: 500,
    rows: 1,
    slidesPerRow: 1,
    variableWidth: true,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
  };
  let activeClassSC = sapChieu === true ? 'active_Film' : '';
  let activeClassDC = dangChieu === true ? 'active_Film' : '';
  return (
    <div>
      <div>
        <button className={`${styleSlick[ activeClassSC ]} px-8 py-3 font-semibold border rounded border-coolGray-800  mr-5`} onClick={() => {
          dispatch({
            type: SET_FILM_SAP_CHIEU
          });
        }}>PHIM ĐANG CHIẾU</button>
        <button className={`${styleSlick[ activeClassDC ]} px-8 py-3 font-semibold border rounded border-coolGray-800  mr-5`} onClick={() => {
          dispatch({
            type: SET_FILM_DANG_CHIEU
          });
        }}>PHIM SẮP CHIẾU</button>
      </div>
      <Slider {...settings} className='h-full'>
        {renderFilms()}
      </Slider>
    </div>
  );
};
export default MultipleRowSlick;
