import React, { useState } from 'react';
import './Film.css';
import _ from 'lodash';
import { history } from '../../App';
export default function Film(props) {
    const { film } = props;
    const [ displayImg, setDisplayImg ] = useState(false);
    const active = displayImg ? 'hidden' : '';
    return (
        <div className={`wrapper ${active}`}>
            <div className="card">
                <img src={film.hinhAnh} alt={film.tenPhim} onError={() => {
                    setDisplayImg(true);
                }} />

                <div className="descriptions">
                    <h1>{film.tenPhim}</h1>
                    {film.moTa.length > 100 ? <p>{film.moTa.slice(0, 100)}...</p> : <p>{film.moTa}</p>}

                    {/* <button>
                        <i className="fab fa-youtube" />
                        Play trailer on YouTube
                    </button> */}
                    <div className='booking-movie'>
                        <button className='h-10 text-red-100 transition-colors duration-150 bg-red-700 rounded-lg focus:shadow-outline hover:bg-red-800 w-full' onClick={() => {
                            history.push(`/details/${film.maPhim}`);
                        }}>
                            Đặt Vé
                        </button>
                    </div>

                </div>
            </div>
        </div>




        // <div className="mr-2 h-full bg-gray-100 bg-opacity-75 px-8 pt-16 pb-24 rounded-lg overflow-hidden text-center relative">
