import React, { useEffect } from 'react';

import { CustomCard } from '@tsamantanis/react-glassmorphism';
import '@tsamantanis/react-glassmorphism/dist/index.css';
import './circle.css';
import { useDispatch, useSelector } from 'react-redux';
import { SET_CHI_TIET_PHIM } from '../../redux/types/QuanLyRapType';
import { layThongTinChiTietPhim } from '../../redux/actions/QuanLyRapAction';
import { Tabs, Radio } from 'antd';
import moment from 'moment';
import { Rate } from 'antd';
import { history } from '../../App';
import style from './Details.module.css';
import { useTranslation } from 'react-i18next';
const { TabPane } = Tabs;

export default function DetailsBooking(props) {
    const { filmDetail } = useSelector(state => state.QuanLyPhimReducer);
    const { t, i18n } = useTranslation();
    const dispatch = useDispatch();
    useEffect(() => {
        let { id } = props.match.params;
        dispatch(layThongTinChiTietPhim(id));
    }, []);
    return (
        <div style={{ backgroundImage: `url(${filmDetail.hinhAnh})`, minHeight: '100vh', backgroundSize: '100%', backgroundPosition: 'center' }} onError={(e) => {

        }}>
            <CustomCard
                style={{ paddingTop: 150, minHeight: '100vh', borderBottom: '0 !important' }}
                effectColor="#181616" // required
                color="#14AEFF" // default color is white
                blur={9} // default blur value is 10px

            >
                <div className='movie_info'>
                    <div className='movie_info_content grid lg:grid-cols-4 gap-3'>
                        <div className='movie-poster col-span-1'>
                            <img src={filmDetail.hinhAnh} alt='images' className='rounded-2xl' onError={(e) => {
                                e.target.src = 'images/movies/transformer.jpg';
                            }} />
                            <div className='flex'>
                                <a href="#booking-details">
                                    <button className='btn-booking-details font-semibold'> <i className='bx bx-movie'></i> {t('booking')}</button>
                                </a>

                                <button className='btn-booking-details font-semibold' onClick={() => {
                                    dispatch({
                                        type: 'OPEN_VIDEO',
                                        video: filmDetail.trailer
                                    });
                                }}> <i className='bx bx-play'></i> {t('trailer')}</button>

                            </div>

                        </div>
                        <div className='movie_details-info text-left col-span-3 text-red-400' >
                            <div className='movie-title text-5xl mb-5 text-red-500'>
                                {filmDetail.tenPhim}
                            </div>
                            <p><span>{t('detail.mota')}: </span>{filmDetail.moTa}</p>
                            <p><span>{t('detail.daodien')}</span>: Joe Russo, Anthony Russo</p>
                            <p><span>{t('detail.theloai')}</span>: Khoa học, Kinh dị, Giật gân</p>
                            <p><span>{t('detail.ngaykhoichieu')}</span>:{moment(filmDetail.ngayKhoiChieu).format('DD-MM-YYYY')} </p>
                            <p><span>{t('detail.thoigian')}</span>: 120 {t('min')}</p>
                            <p><span>{t('detail.ngonngu')}</span>: Tiếng Việt,Tiếng Anh</p>
                            <p><span>{t('detail.danhgia')}</span>: {filmDetail.danhGia} IMDB </p>

                        </div>
                        <div className='col-span-2 col-start-2  '>
                            <h1 className='text-center' style={{ color: 'yellow', fontWeight: 'bold', fontSize: 15 }}>{t('detail.danhgia')}</h1>
                            <h1 className=' text-center'><Rate allowHalf value={filmDetail.danhGia / 2} style={{ color: '#78ed78', fontSize: 30 }} /></h1>
                            <div className={`c100 p${filmDetail.danhGia * 10} green big`} style={{ marginLeft: '26%' }}>
                                <span>{filmDetail.danhGia}</span>
                                <div class="slice">
                                    <div class="bar"></div>
                                    <div class="fill"></div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card-container mr-auto mt-20 ml-auto mr-auto w-2/3 rounded-lg bg-gray-200 py-5 px-5" id='booking-details'>
                    <Tabs type="card" centered>
                        <TabPane tab={t('showtime')} key="1">
                            <div >
                                <Tabs tabPosition={'top'} size='small' type='line'>
                                    {filmDetail.heThongRapChieu?.map((htr, index) => {
                                        return <TabPane tab={<div className='flex flex-col items-center'>
                                            <img src={htr.logo} alt='123' width={50} height={50} />
                                            {htr.tenHeThongRap}

                                        </div>} key={index}>
                                            {htr.cumRapChieu?.map((cumRap, index) => {
                                                return <div className='mt-5' key={index}>
                                                    <div className='flex flex-row'>
                                                        <img src={cumRap.hinhAnh} alt='123' style={{ width: 50, height: 50 }} />
                                                        <div className='ml-2'>
                                                            <p className='font-bold mb-0' style={{ fontSize: 20 }}> {cumRap.tenCumRap}</p>
                                                            <p className='mt-0 text-gray-400'> {cumRap.diaChi}</p>
                                                        </div>
                                                    </div>
                                                    <div className='thongtinlichchieu grid lg:grid-cols-4 grid-cols-2'>
                                                        {cumRap.lichChieuPhim?.map((lichChieu, index) => {
                                                            return <button style={{ backgroundColor: 'rgb(253, 183, 59)', border: '1px solid rgba(0, 0, 0, 0.23)', padding: '5px 15px' }} className='m-5 font-bold rounded-lg' onClick={() => {
                                                                history.push(`/checkout/${lichChieu.maLichChieu}`);
                                                            }}>
                                                                {moment(lichChieu.ngayChieuGioChieu).format('hh:mm A')}
                                                            </button>;
                                                        })}
                                                    </div>
                                                </div>;
                                            })}
                                        </TabPane>;
                                    })}
                                </Tabs>
                            </div>
                        </TabPane>

                    </Tabs>
                </div>

            </CustomCard>


        </div>
    );
}
