import { SET_DANH_SACH_PHIM, SET_FILM_DANG_CHIEU, SET_FILM_SAP_CHIEU, SET_THONG_TIN_PHIM } from "../types/QuanLyPhimType";
import { SET_CHI_TIET_PHIM } from "../types/QuanLyRapType";

const initialState = {
    arrFilm: [
    ],
    dangChieu: true,
    sapChieu: true,
    arrDefault: [],
    filmDetail: {},
    thongTinPhim: {},
    filmBanner: {}

};

export default (state = initialState, action) => {
    switch (action.type) {

        case SET_DANH_SACH_PHIM:
            state.arrFilm = action.arrFilm;
            state.arrDefault = state.arrFilm;
            return { ...state };
            break;
        case SET_FILM_DANG_CHIEU: {
            state.dangChieu = !state.dangChieu;
            state.arrFilm = state.arrDefault.filter(film => film.dangChieu === state.dangChieu);

            return { ...state };
            break;
        }
        case SET_FILM_SAP_CHIEU: {
            state.sapChieu = !state.sapChieu;
            state.arrFilm = state.arrDefault.filter(film => film.sapChieu === state.sapChieu);
            return { ...state };
        }
        case SET_CHI_TIET_PHIM: {
            state.filmDetail = action.filmDetail;
            return { ...state };
        }
        case SET_THONG_TIN_PHIM: {
            state.thongTinPhim = action.thongTinPhim;
            return { ...state };
        }

        default:
            return state;
    }
};
