import { GROUPID } from "../util/settings/config";
import { baseService } from "./baseService";


class QuanLyNguoiDungService extends baseService {
    constructor() {
        super();
    }
    dangNhap = (thongtinDangNhap) => {
        return this.post(`/api/QuanLyNguoiDung/DangNhap`, thongtinDangNhap);
    };
    dangKy = (thongtinDangNhap) => {
        return this.post(`/api/QuanLyNguoiDung/DangNhap`, thongtinDangNhap);
    };
    LayThongTinTaiKhoan = () => {
        return this.post(`/api/QuanLyNguoiDung/ThongTinTaiKhoan`);
    };
    layDanhSachNguoiDung = (tuKhoa) => {
        if (tuKhoa == '') {
            return this.get(`/api/QuanLyNguoiDung/LayDanhSachNguoiDung?MaNhom=${GROUPID}`);
        }
        return this.get(`/api/QuanLyNguoiDung/LayDanhSachNguoiDung?MaNhom=${GROUPID}&tuKhoa=${tuKhoa}`);
    };
    LayDanhSachLoaiNguoiDung = () => {
        return this.get(`/api/QuanLyNguoiDung/LayDanhSachLoaiNguoiDung`);
    };
    CapNhatNguoiDung = (userEdit) => {
        return this.put(`/api/QuanLyNguoiDung/CapNhatThongTinNguoiDung`, userEdit);
    };
    ThemNguoiDung = (user) => {
        return this.post(`/api/QuanLyNguoiDung/ThemNguoiDung`, user);
    };

}

export const quanLyNguoiDungService = new QuanLyNguoiDungService();
