import React from 'react';
import { Carousel } from 'antd';

export default function Mobile() {
    const contentStyle = {
        height: '100%',
        color: '#fff',
        lineHeight: '160px',
        textAlign: 'center',
        background: 'black',
    };
    const renderImg = () => {
        for (let i = 1; i <= 16; i++) {
            return <img src={`https://tix.vn/app/assets/img/icons/slide/slide${i}.jpg`} alt='123' />;


        }
    };
    return (
        <div id='homeApp'>
            <div className='main-mobile'>
                <div className='grid grid-cols-2 gap-5 text-white px-20'>
                    <div className='left'>
                        <p class="textLeft">Ứng dụng tiện lợi dành cho</p>
                        <p class="textLeft">người yêu điện ảnh</p>

                    </div>
                    <div className='right'>
                        <img class="img-responsive phone-img" src="https://tix.vn/app/assets/img/icons/mobile.png" alt="Loading..."></img>

                        <Carousel autoplay className='sliderScreen' style={{ height: '500px' }}>

                            <img src='https://tix.vn/app/assets/img/icons/slide/slide3.jpg' alt style={{ sty }} />

                            <div>
                                <h3 style={contentStyle}>2</h3>
                            </div>
                            <div>
                                <h3 style={contentStyle}>3</h3>
                            </div>
                            <div>
                                <h3 style={contentStyle}>4</h3>
                            </div>
                        </Carousel>


                    </div>
                </div>
            </div>
        </div>

    );
}
