import React, { Fragment } from 'react';
import { Tabs, Radio, Space } from 'antd';
import { NavLink } from 'react-router-dom';
import moment from 'moment';
const { TabPane } = Tabs;

export default function HomeMenu(props) {
    const { heThongRapChieu } = props;
    console.log("file: HomeMenu.js ~ line 7 ~ HomeMenu ~ heThongRapChieu", heThongRapChieu);
    const renderHeThongRap = () => {
        return heThongRapChieu?.map((heThongRap, index) => {
            return <TabPane tab={<img src={heThongRap.logo} alt={heThongRap.maHeThongRap} className='rounded-full w-16' />} key={index}>
                <Tabs tabPosition={'left'}>

                    {heThongRap.lstCumRap?.map((cumRap, index) => {
                        return <TabPane tab={
                            <div style={{ width: '300' }} className='flex'>
                                <img src={cumRap.hinhAnh} alt='123' width="50" />
                                <div className='text-left  ml-2'>
                                    {cumRap.tenCumRap}
                                    <p className='text-red'>Chi tiáº¿t</p>
                                </div>
                            </div>
                        } key={index}>
                            {cumRap.danhSachPhim?.slice(3, 8).map((film, index) => {
                                return <Fragment key={index}>
                                    <div className='flex my-5' >
                                        <div className='flex'>
                                            <img src={film.hinhAnh} alt='123' width='50' height='50' onError={(e) => { e.target.onerror = null; e.target.src = "http://movieapi.cyberlearn.vn/hinhanh/bo-gia_gp01.jpg"; }} />
                                            <div className='ml-4'>
                                                <h3 className='  text-green-500'>{film.tenPhim}</h3>
                                                <p>{cumRap.diaChi}</p>
                                                <div className='grid grid-cols-5 gap-5'>
                                                    {film.lstLichChieuTheoPhim?.slice(0, 10).map((lichChieu, index) => {
                                                        return <NavLink to='/' key='index' className='text-indigo-600'>
                                                            {moment(lichChieu.ngayChieuGioChieu).format('hh:mm A')}
                                                        </NavLink>;
                                                    })}
                                                </div>

                                            </div>

                                        </div>
                                    </div>
                                    <hr />
                                </Fragment>;

                            })}
                        </TabPane>;
                    })}
                </Tabs>
            </TabPane>;
        });
    };
    return (
        <>

            <Tabs tabPosition={'left'}>
                {renderHeThongRap()}
            </Tabs>
        </>
    );
}
