import React from 'react';
import '../Login/Login.css';
import './Register.css';
import { history } from '../../App';
import { useFormik } from 'formik';

import * as Yup from 'yup';
export default function Register() {
    const SignupSchema = Yup.object().shape({
        taiKhoan: Yup.string()
            .required('Bạn chưa điền tài khoản'),
        matKhau: Yup.string()
            .required('Bạn chưa điền mật khẩu'),
    });
    const formik = useFormik({
        initialValues: {
            taiKhoan: '',
            matKhau: ''
        },
        validationSchema: SignupSchema,
        onSubmit: values => {


        },
    });

    return (
        <div className='register-wrapper'>

            <img className="signin--header ml-auto mr-auto" src="https://tix.vn/app/assets/img/login/group@2x.png" />


            <form className='font-sans'>
                <h2 class="text-center text-4xl text-blue-300 font-display font-semibold  xl:text-2xl
                    xl:text-bold">Đăng Ký</h2>
                <div className='grid grid-cols-2 gap-3'>
                    <div className="mb-4">
                        <label className="block text-white text-sm font-display font-semibold mb-2 text-left" htmlFor="username">
                            Email
                        </label>
                        <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" type="text" placeholder="abc@gmail.com...." />
                    </div>
                    <div className="mb-4">
                        <label className="block text-white text-sm font-display font-semibold mb-2 text-left" htmlFor="username">
                            Số điện thoại
                        </label>
                        <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" type="text" placeholder="+84............" />
                    </div>
                    <div className="mb-4">
                        <label className="block text-white text-sm font-display font-semibold mb-2 text-left" htmlFor="username">
                            Họ tên
                        </label>
                        <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" type="text" placeholder="Nguyễn Văn A....." />
                    </div>
                    <div className="mb-4">
                        <label className="block text-white text-sm font-display font-semibold mb-2 text-left" htmlFor="username">
                            Tài Khoản
                        </label>
                        <input className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" id="username" type="text" placeholder="Username" />
                    </div>
                    <div className="mb-2">
                        <label className="block  text-sm font-display font-semibold mb-2 text-left text-white" htmlFor="password">
                            Mật Khẩu
                        </label>
                        <input className="shadow appearance-none border border-red-500 rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline" id="password" type="password" placeholder="******************" />
                        <p className="text-red-500 text-xs italic mb-0">Please choose a password.</p>
                    </div>
                    <div className="mb-2">
                        <label className="block  text-sm font-display font-semibold mb-2 text-left text-white" htmlFor="password">
                            Xác nhận mật khẩu
                        </label>
                        <input className="shadow appearance-none border border-red-500 rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline" id="password" type="password" placeholder="******************" />
                    </div>

                    <div class="text-sm font-display font-semibold text-white text-center mb-5 col-span-2">
                        Bạn đã có tài khoản ? <a class="cursor-pointer text-indigo-600 hover:text-indigo-800" onClick={() => {
                            history.push('/login');
                        }}>Đăng Nhập</a>
                    </div>
                </div>
                <button class="bg-blue-500 hover:bg-blue-700 text-white font-display font-semibold py-2 px-4 rounded focus:outline-none focus:shadow-outline mb-5" type="button">
                    Đăng ký
                </button>
            </form>
            <div className="signin-close" onClick={() => {
                history.push('/');
            }} />
        </div>
    );
}
