import { DISPLAY_LOADING, HIDE_LOADING } from "../../util/settings/config";

const initialState = {
    isLoading: false
};

export default (state = initialState, { type, payload }) => {
    switch (type) {

        case DISPLAY_LOADING: {
            return { ...state, isLoading: true };
            break;
        }
        case HIDE_LOADING: {
            return { ...state, isLoading: false };
            break;
        }




        default:
            return state;
    }
};
