import React, { useEffect } from 'react';
import { Carousel } from 'antd';
import { useSelector } from 'react-redux';
import axios from 'axios';
const contentStyle = {
    height: '700px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    backgroundPosition: 'center',
    backgroundSize: '100%',
    backgroundRepeat: 'no-repeat'
};

export default function HomeCarousel() {
    const { listBanner } = useSelector(state => state.CarouselReducer);

    //tự kích hoạt khi component load ra
    useEffect(async () => {
        try {
            const result = await axios({
                url: 'http://movieapi.cyberlearn.vn/api/QuanLyPhim/LayDanhSachBanner',
                method: 'GET'
            });
            console.log(result);
        } catch (error) {

        }
    }, []);

    const renderImg = () => {
        return listBanner.map((item, index) => {
            return <div key={index}>
                <div style={{ ...contentStyle, backgroundImage: `url(${item.hinhAnh})` }}>
                    {/* <img src={item.hinhAnh} className="w-full opacity-0" alt={item.hinhAnh} /> */}
                </div>
            </div>;
        });
    };
    return (
        <Carousel effect="fade">
            {renderImg()}

        </Carousel>
    );
}
