import { SET_DANH_SACH_PHIM, SET_FILM_DANG_CHIEU } from "../types/QuanLyPhimType";

const initialState = {
    arrFilm: [
        {
            "maPhim": 9456,
            "tenPhim": "bố già 22",
            "biDanh": "bo-gia-22",
            "trailer": "https://www.youtube.com/embed/S3AVcCggRnU",
            "hinhAnh": "http://movieapi.cyberlearn.vn/hinhanh/bo-gia-22_gp01.jpg",
            "moTa": "312321",
            "maNhom": "GP01",
            "ngayKhoiChieu": "2021-10-06T00:00:00",
            "danhGia": 10,
            "hot": true,
            "dangChieu": true,
            "sapChieu": true
        }
    ]
};

export default (state = initialState, action) => {
    switch (action.type) {

        case SET_DANH_SACH_PHIM:
            return { ...state, arrFilm: action.arrFilm };
        case SET_FILM_DANG_CHIEU: {
            return { ...state };
        }
        default:
            return state;
    }
};
