import React, { Fragment, useEffect } from 'react';
import { Table, Input } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { layDanhSachPhimAction, XoaPhimAction } from '../../../redux/actions/QuanLyPhimAction';
import { NavLink } from 'react-router-dom';
import { EditTwoTone, DeleteTwoTone, CalendarOutlined, DeleteOutlined } from '@ant-design/icons';

const { Search } = Input;

export default function Films() {
    const { arrDefault } = useSelector(state => state.QuanLyPhimReducer);
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(layDanhSachPhimAction());
    }, []);
    const data = arrDefault;
    function onChange(pagination, filters, sorter, extra) {
        console.log('params', pagination, filters, sorter, extra);
    }
    const columns = [
        {
            title: 'Mã phim',
            dataIndex: 'maPhim',
            filterMode: 'tree',
            filterSearch: true,
            sorter: (a, b) => a.maPhim - b.maPhim,
            width: '10%',
        },
        {
            title: 'Hình ảnh',
            dataIndex: 'hinhAnh',
            render: (text, films, index) => {
                return <Fragment>
                    <img src={films.hinhAnh} alt={text} width={50} height={50} onError={(e) => {
                        e.target.onError = null;
                        e.target.src = `https://picsum.photos/id/${index}/50/50`;
                    }} />

                </Fragment>;
            },
        },
        {
            title: 'Tên phim',
            dataIndex: 'tenPhim',
            sorter: (a, b) => a.tenPhim.length - b.tenPhim.length,

        },
        {
            title: 'Mô tả',
            dataIndex: 'moTa',
            render: (text, film, index) => {
                return <Fragment>
                    {film.moTa.length > 50 ? film.moTa.substr(0, 50) + '....' : film.moTa}
                </Fragment>;
            },
            sorter: (a, b) => a.moTa.length - b.moTa.length,

        },
        {
            title: 'Trailer',
            dataIndex: 'trailer',
            render: (text, film, index) => {
                return <a href={film.trailer} target="_blank">
                    Link trailer
                </a>;
            },

        },
        {
            title: 'Hành động',
            dataIndex: 'hanhDong',
            render: (text, film, index) => {
                return <Fragment>
                    <NavLink key='1' className='text-2xl' to={`/mainAdmin/films/edit/${film.maPhim}`}><EditTwoTone style={{ color: 'blue' }} /></NavLink>
                    <span key='2' to="/" className='text-2xl mx-5' onClick={() => {
                        if (window.confirm('Bạn có chắc chắc muốn xóa phim ' + film.tenPhim)) {
                            dispatch(XoaPhimAction(film.maPhim));
                        }
                    }}><DeleteOutlined style={{ color: 'red' }} /></span>
                    <NavLink key='3' to={`/mainAdmin/showtimes/${film.maPhim}/${film.tenPhim}`} className='text-2xl'><CalendarOutlined style={{ color: 'green' }} /></NavLink>
                </Fragment>;
            },

        },
    ];
    const onSearch = (value) => {
        dispatch(layDanhSachPhimAction(value));
    };

    return (
        <div>
            <h3>Quản lý phim</h3>
            <Search
                className='my-5'
                placeholder="input search text"
                allowClear
                enterButton="Search"
                size="large"
                onSearch={onSearch}
            />
            <Table columns={columns} dataSource={data} onChange={onChange} />
        </div>
    );
}
