import React, { useEffect, useState } from 'react';
import { Tabs, Radio, Space } from 'antd';
import { useFormik } from 'formik';
import { CapNhatNguoiDungAction, thongTinTaiKhoanAction } from '../../redux/actions/QuanLyNguoiDungAction';
import { useDispatch, useSelector } from 'react-redux';
import { GROUPID } from '../../util/settings/config';
import * as Yup from 'yup';
import Swal from 'sweetalert2';
const { TabPane } = Tabs;
export default function Profile(props) {
    const dispatch = useDispatch();
    const { thongTinTaiKhoan, userLogin } = useSelector(state => state.QuanLyNguoiDungReducer);
    const [ disabled, setDisabled ] = useState(true);
    useEffect(() => {
        dispatch(thongTinTaiKhoanAction());
    }, []);
    const SignupSchema = Yup.object().shape({
        hoTen: Yup.string()
            .required('Bạn chưa điền họ tên').min(2, 'Kí tự tên quá ngắn!')
            .max(50, 'Kí tự tên quá dài!'),
        taiKhoan: Yup.string()
            .required('Bạn chưa điền tài khoản').min(2, 'Kí tự tài khoản quá ngắn!')
            .max(50, 'Kí tự tài khoản quá dài!'),
        matKhau: Yup.string()
            .required('Bạn chưa điền mật khẩu').min(5, 'Kí tự mật khẩu quá ngắn!')
            .max(50, 'Kí tự mật khẩu quá dài!'),
        email: Yup.string()
            .required('Bạn chưa điền email').email('Email không dúng!'),
        soDt: Yup.string().required('Bạn chưa điền số điện thoại').matches(
            /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/,
            "Số điện thoại không đúng"
        ),
        xacNhanMatKhau: Yup.string()
            .oneOf([ Yup.ref('matKhau'), null ], 'Mật khẩu xác nhận không đúng!')
    });
    const formik = useFormik({
        enableReinitialize: true,
        initialValues: {
            taiKhoan: thongTinTaiKhoan?.taiKhoan,
            matKhau: thongTinTaiKhoan?.matKhau,
            hoTen: thongTinTaiKhoan?.hoTen,
            email: thongTinTaiKhoan?.email,
            soDt: thongTinTaiKhoan?.soDT,
            maNhom: GROUPID,
            maLoaiNguoiDung: userLogin.maLoaiNguoiDung,
            xacNhanMatKhau: ''
        },
        validationSchema: SignupSchema,
        onSubmit: (values) => {
            Swal.fire({
                icon: 'question',
                title: 'Cập nhật!',
                text: 'Bạn có muốn cập nhật thông tin tài khoản!',
                showCancelButton: true,
                confirmButtonText: 'Ok',
                cancelButtonText: 'Không'
            }).then((info) => {
                if (info.isConfirmed) {
                    dispatch(CapNhatNguoiDungAction(values));
                }

            });
            ;

        },
        onReset: (values) => {
            console.log(values);

        }


    });
    const handleChangepass = (e) => {
        e.preventDefault();
        if (formik.errors.matKhau) {
            return;
        } else {
            Swal.fire({
                icon: 'question',
                title: 'Đổi mật khẩu!',
                text: 'Bạn có muốn đổi mật khẩu!',
                showCancelButton: true,
                confirmButtonText: 'Ok',
                cancelButtonText: 'Không'
            }).then((info) => {
                if (info.isConfirmed) {
                    dispatch(CapNhatNguoiDungAction(formik.values));
                }


            });
            ;
        }
    };
    const handleBlur = () => {
        setDisabled(false);
    };
    let disabledClass = disabled ? 'disable-btn cursor-not-allowed' : '';
    return (
        <div style={{ height: 550, margin: "65px", padding: "30px", backgroundColor: 'white', borderRadius: 20 }} className='profile'>
            <Tabs tabPosition={"left"}>
                <TabPane tab={<div style={{ textTransform: 'uppercase', fontSize: 14 }} className='text-gray-600 font-semibold '>
                    Thông tin tài khoản
                </div>} key="1">
                    <div className='flex justify-center items-center'>
                        <form className="w-full max-w-lg" onSubmit={formik.handleSubmit}>
                            <h3 className='text-center'>THÔNG TIN CỦA BẠN</h3>
                            <div className="flex flex-wrap -mx-3 mb-6">
                                <div className="w-full md:w-1/2 px-3 mb-6 md:mb-0">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-first-name">
                                        Họ tên
                                    </label>
                                    <input name='hoTen' onChange={formik.handleChange} onBlur={handleBlur} value={formik.values.hoTen} className="appearance-none block w-full bg-gray-200 text-gray-700 border  rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" id="grid-first-name" type="text" placeholder="Jane" />
                                    <p className="text-red-500 text-xs italic">{formik.errors.hoTen}</p>
                                </div>
                                <div className="w-full md:w-1/2 px-3">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-last-name">
                                        Email
                                    </label>
                                    <input value={formik.values.email} onBlur={handleBlur} name='email' onChange={formik.handleChange} className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="text" placeholder="Doe@gmail.com" />
                                    <p className="text-red-500 text-xs italic">{formik.errors.email}</p>
                                </div>
                            </div>
                            <div className="flex flex-wrap -mx-3 mb-6">
                                <div className="w-full md:w-1/2 px-3">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-password">
                                        Tài khoản
                                    </label>
                                    <input onBlur={handleBlur} value={formik.values.taiKhoan} name='taiKhoan' onChange={formik.handleChange} disabled='true' className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-password" type="text" placeholder="HelloTix123" />
                                    <p className="text-gray-600 text-xs italic">Không thể chỉnh sửa</p>
                                </div>
                                <div className="w-full md:w-1/2 px-3">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-last-name">
                                        Số điện thoại
                                    </label>
                                    <input onBlur={handleBlur} value={formik.values.soDt} name='soDt' onChange={formik.handleChange} className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="number" placeholder="08659525" />
                                    <p className="text-red-500 text-xs italic">{formik.errors.soDt}</p>
                                </div>
                            </div>
                            <div className="md:w-2/3 text-center">

                                <button disabled={disabled} className={`shadow bg-yellow-600  focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded ${disabledClass}`} type="submit">
                                    Cập nhật
                                </button>
                            </div>


                        </form>

                    </div>
                </TabPane>
                <TabPane tab={<div style={{ textTransform: 'uppercase', fontSize: 14 }} className='text-gray-600 font-semibold'>
                    Đổi mật khẩu
                </div>} key="2">
                    <div className='flex justify-center items-center'>
                        <form className="w-full max-w-lg" onSubmit={handleChangepass}>
                            <h3 className='text-center'>ĐỔI MẬT KHẨU</h3>
                            <div className="flex flex-wrap -mx-3 mb-6">
                                <div className="md:w-full w-1/2 px-3 mb-6 md:mb-0">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-first-name">
                                        Mật khẩu cũ
                                    </label>
                                    <input disabled name='hoTen' onChange={formik.handleChange} value={formik.values.matKhau} className="appearance-none block w-full bg-gray-200 text-gray-700 border  rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" id="grid-first-name" type="text" placeholder="Jane" />
                                    <p className="text-gray-600 text-xs italic">Không thể chỉnh sửa</p>
                                </div>
                                <div className="md:w-full w-1/2 px-3">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-last-name">
                                        Nhập mật khẩu mới
                                    </label>
                                    <input name='matKhau' onChange={formik.handleChange} className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="password" placeholder="*************" />
                                    <p className="text-red-500 text-xs italic">{formik.errors.matKhau}</p>
                                </div>
                                <div className="md:w-full w-1/2 px-3">
                                    <label className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2" htmlFor="grid-last-name">
                                        Xác nhận mật khẩu
                                    </label>
                                    <input name='xacNhanMatKhau' onChange={formik.handleChange} className="appearance-none block w-full bg-gray-200 text-gray-700 border border-gray-200 rounded py-3 px-4 leading-tight focus:outline-none focus:bg-white focus:border-gray-500" id="grid-last-name" type="password" placeholder="*************" />
                                    <p className="text-red-500 text-xs italic">{formik.errors.xacNhanMatKhau}</p>
                                </div>
                            </div>

                            <div className="md:w-2/3 text-center">

                                <button className={`shadow bg-yellow-600  focus:shadow-outline focus:outline-none text-white font-bold py-2 px-4 rounded`} type="submit">
                                    Đổi mật khẩu
                                </button>
                            </div>


                        </form>
                    </div>
                </TabPane>

            </Tabs>
        </div>
    );
}
