import React, { Fragment, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { history } from '../../../../App';
import { Select } from 'antd';
import { useTranslation } from 'react-i18next';
import { useSelector } from 'react-redux';
import _ from 'lodash';
import { Popover, Button } from 'antd';
import { UserOutlined } from '@ant-design/icons';

import { TOKEN, USER_LOGIN } from '../../../../util/settings/config';
const { Option } = Select;
export default function Header(props) {
    const { userLogin } = useSelector(state => state.QuanLyNguoiDungReducer);
    const { t, i18n } = useTranslation();
    const handleChange = (e) => {
        i18n.changeLanguage(e.target.value);
    };
    const renderLogin = () => {
        if (_.isEmpty(userLogin)) {
            return <Fragment>
                <button className="self-center px-8 py-3 rounded" onClick={() => {
                    history.push('/login');
                }}>{t('signin')}</button>
                <button className="self-center px-8 py-3 font-semibold rounded bg-violet-600 text-coolGray-50" onClick={() => {
                    history.push('/register');
                }}>{t('signup')}</button>
            </Fragment>;
        }
        return <Fragment>
            <button className="self-center px-8 py-3 rounded" onClick={() => {
                history.push('/profile');
            }}>Xin chào {userLogin.taiKhoan}</button>
            <button onClick={() => {
                localStorage.removeItem(USER_LOGIN);
                localStorage.removeItem(TOKEN);
                history.push('/home');
                window.location.reload();
            }} className="text-green-600 mr-5">Đăng xuất</button>
        </Fragment>;
    };
    const text = <span>Title</span>;
    const content = (
        <div>
            <p>Content</p>
            <p>Content</p>
        </div>
    );
