        <header className="nav-wrapper">
            <div className="container">
                <div className="nav">
                    <a href="#" className="logo">
                        <i className="bx bx-movie-play bx-tada main-color" />
                        T<span className="main-color">i</span>x
                    </a>
                    <ul className="nav-menu pt-5" id="nav-menu">
                        <li><a href="#">{t('home')}</a></li>
                        <li><a href="#">{t('movie')}</a></li>
                        <li><a href="#">{t('theater')}</a></li>
                        <li><a href="#">{t('contact')}</a></li>
                        <li>
                            <Popover placement="bottom" title={text} content={content} trigger="click">
                                <div style={{ cursor: 'pointer' }}>
                                    <UserOutlined className='font-bold text-2xl' /> <span style={{ fontWeight: 700, textTransform: 'uppercase', marginTop: 5 }}>{userLogin.taiKhoan}</span>
                                </div>
                            </Popover>
                        </li>
                        {/* <li>
                            <a className="btn btn-hover" onClick={() => {
                                history.push('/login');
                            }}>
                                <span>{t('signin')}</span>
                            </a>
                        </li>
                        <li>
                            <a className="btn btn-hover" onClick={() => {
                                history.push('/register');
                            }}>
                                <span>{t('signup')}</span>
                            </a>
                        </li> */}
                        <li>
                            <select className='select-language' onChange={handleChange} defaultValue='vietnam'>
                                <option value="vietnam">
                                    {t('vietnamese')}
                                </option>
                                <option value="china"> {t('chinese')}</option>
                                <option value="english"> {t('english')}</option>
                            </select>

                        </li>
                    </ul>
                    <div className="hamburger-menu" id="hamburger-menu">
                        <div className="hamburger" />
                    </div>
                </div>
            </div>
