import React, { Fragment, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { history } from '../../../../App';
import { Select } from 'antd';
import { useTranslation } from 'react-i18next';
import { useSelector } from 'react-redux';
import _ from 'lodash';
import { Popover, Button } from 'antd';
import { UserOutlined } from '@ant-design/icons';

import { TOKEN, USER_LOGIN } from '../../../../util/settings/config';
const { Option } = Select;
export default function Header(props) {
    const { userLogin } = useSelector(state => state.QuanLyNguoiDungReducer);
    const { t, i18n } = useTranslation();
    const handleChange = (e) => {
        i18n.changeLanguage(e.target.value);
    };
    const text = <div style={{ fontWeight: 700, textTransform: 'uppercase', textAlign: 'center' }}>
        <a>Tài Khoản</a>
    </div>;
    const content = (
        <div style={{ fontWeight: 700, textTransform: 'uppercase', textAlign: 'center' }} className='content-popover'>
            <p onClick={() => {
                history.push('/profile');
            }}>Thông tin tài khoản</p>
            <p>Đổi mật khẩu</p>
            <p onClick={() => {
                localStorage.removeItem(USER_LOGIN);
                localStorage.removeItem(TOKEN);
                history.push('/home');
                window.location.reload();
            }}>Đăng xuất</p>
        </div>
    );
    const renderLogin = () => {
        if (_.isEmpty(userLogin)) {
            return <Fragment>
                <li>
                    <a className="btn btn-hover" onClick={() => {
                        history.push('/login');
                    }}>
                        <span>{t('signin')}</span>
                    </a>
                </li>
                <li>
                    <a className="btn btn-hover" onClick={() => {
                        history.push('/register');
                    }}>
                        <span>{t('signup')}</span>
                    </a>
                </li>
            </Fragment>;
        }
        return <Fragment>
            <li>
                <Popover placement="bottom" title={text} content={content} trigger="click">
                    <a style={{ cursor: 'pointer' }} className='flex items-center'>
                        <UserOutlined className='font-bold text-2xl' /> <span style={{ fontWeight: 700, textTransform: 'uppercase' }}>{userLogin.taiKhoan}</span>
                    </a>
                </Popover>
            </li>
        </Fragment>;
    };

    return (
        <header className="nav-wrapper">
            <div className="container">
                <div className="nav">
                    <a href="#" className="logo">
                        <i className="bx bx-movie-play bx-tada main-color" />
                        T<span className="main-color">i</span>x
                    </a>
                    <ul className="nav-menu pt-5 hidden sm:block" id="nav-menu">
                        <li><a href="#">{t('home')}</a></li>
                        <li><a href="#">{t('movie')}</a></li>
                        <li><a href="#">{t('theater')}</a></li>
                        <li><a href="#">{t('contact')}</a></li>
                        {renderLogin()}

                        <li>
                            <select className='select-language' onChange={handleChange} defaultValue='vietnam'>
                                <option value="vietnam">
                                    {t('vietnamese')}
                                </option>
                                <option value="china"> {t('chinese')}</option>
                                <option value="english"> {t('english')}</option>
                            </select>

                        </li>
                    </ul>
                    {/* <div className="hamburger-menu" id="hamburger-menu">
                        <div className="hamburger" />
                    </div> */}
                    {/* Mobile menu, show/hide based on menu state. */}
                    <button type="button" className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white" aria-controls="mobile-menu" aria-expanded="false">
                        <span className="sr-only">Open main menu</span>
                        {/*
      Icon when menu is closed.

      Heroicon name: outline/menu

      Menu open: "hidden", Menu closed: "block"
    */}
                        <svg className="block h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
