import React, { useEffect } from 'react';

import { CustomCard } from '@tsamantanis/react-glassmorphism';
import '@tsamantanis/react-glassmorphism/dist/index.css';
import './circle.css';
import { useDispatch, useSelector } from 'react-redux';
import { SET_CHI_TIET_PHIM } from '../../redux/types/QuanLyRapType';
import { layThongTinChiTietPhim } from '../../redux/actions/QuanLyRapAction';
import { Tabs, Radio } from 'antd';
import moment from 'moment';
import { Rate } from 'antd';
import { history } from '../../App';

const { TabPane } = Tabs;

export default function DetailsBooking(props) {
    const { filmDetail } = useSelector(state => state.QuanLyPhimReducer);
    console.log(filmDetail);
    const dispatch = useDispatch();
    useEffect(() => {
        let { id } = props.match.params;
        dispatch(layThongTinChiTietPhim(id));
    }, []);
    return (
        <div style={{ backgroundImage: `url(${filmDetail.hinhAnh})`, minHeight: '100vh', backgroundSize: '100%', backgroundPosition: 'center' }}>
            <CustomCard
                style={{ paddingTop: 150, minHeight: '100vh', borderRadius: '0 !important' }}
                effectColor="#C780FF" // required
                color="#14AEFF" // default color is white
                blur={10} // default blur value is 10px

            >
                <div className='grid grid-cols-12'>
                    <div className='col-span-5 col-start-2'>
                        <div className='grid grid-cols-3'>
                            <img className='col-span-1' src={filmDetail.hinhAnh} alt='alo' style={{ width: '100%', height: 230 }} />
                            <div className='text-black col-span-2 flex flex-col justify-center ml-5'>
                                <p>Ngày chiếu: {moment(filmDetail.ngayKhoiChieu).format('DD.MM.YYYY')}</p>
                                <p>{filmDetail.tenPhim}</p>
                                <p>{filmDetail.moTa}</p>
                            </div>
                        </div>
                    </div>
                    <div className='col-span-4'>
                        <h1 className='col-end-10 text-center' style={{ color: 'yellow', fontWeight: 'bold', fontSize: 15 }}>Đánh giá</h1>
                        <h1 className=' text-center'><Rate allowHalf value={filmDetail.danhGia / 2} style={{ color: '#78ed78', fontSize: 30 }} /></h1>
                        <div className={`c100 p${filmDetail.danhGia * 10} green big`} style={{ marginLeft: '22%' }}>
                            <span>{filmDetail.danhGia}</span>
                            <div class="slice">
                                <div class="bar"></div>
                                <div class="fill"></div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card-container mr-auto mt-20 ml-auto mr-auto w-2/3 bg-white py-5 px-5" >
                    <Tabs type="card" centere>
                        <TabPane tab="Lịch Chiếu" key="1">
                            <div >
                                <Tabs tabPosition={'left'} size='small' type='line'>
                                    {filmDetail.heThongRapChieu?.map((htr, index) => {
                                        return <TabPane tab={<div>
                                            <img src={htr.logo} alt='123' width={50} height={50} />
                                            {htr.tenHeThongRap}

                                        </div>} key={index}>
                                            {htr.cumRapChieu?.map((cumRap, index) => {
                                                return <div className='mt-5' key={index}>
                                                    <div className='flex flex-row'>
                                                        <img src={cumRap.hinhAnh} alt='123' style={{ width: 50, height: 50 }} />
                                                        <div className='ml-2'>
                                                            <p className='font-bold mb-0' style={{ fontSize: 20 }}> {cumRap.tenCumRap}</p>
                                                            <p className='mt-0 text-gray-400'> {cumRap.diaChi}</p>
                                                        </div>
                                                    </div>
                                                    <div className='thongtinlichchieu grid grid-cols-4'>
                                                        {cumRap.lichChieuPhim?.map((lichChieu, index) => {
                                                            return <button style={{ backgroundColor: 'rgb(253, 183, 59)', border: '1px solid rgba(0, 0, 0, 0.23)', padding: '5px 15px' }} className='m-5 font-bold rounded-lg' onClick={() => {
                                                                history.push(`/details/${filmDetail.maPhim}`);
                                                            }}>
                                                                {moment(lichChieu.ngayChieuGioChieu).format('hh:mm A')}
                                                            </button>;
                                                        })}
                                                    </div>
                                                </div>;
                                            })}
                                        </TabPane>;
                                    })}
                                </Tabs>
                            </div>
                        </TabPane>
                        <TabPane tab="Thông Tin" key="2">

                        </TabPane>
                    </Tabs>
                </div>

            </CustomCard>


        </div>
    );
}
