import { connection } from "../../index";
import { quanLyDatVeService } from "../../service/QuanLyDatVeService";
import { DISPLAY_LOADING, HIDE_LOADING } from "../../util/settings/config";
import { CHUYEN_TAB, DAT_GHE, DAT_VE_HOAN_TAT, SET_CHI_TIET_PHONG_VE } from "../types/QuanLyDatVeType";
import { thongTinTaiKhoanAction } from "./QuanLyNguoiDungAction";
import Swal from 'sweetalert2';

export const layChiTietPhongVeAction = (maLichChieu) => {
    return async (dispatch) => {
        try {
            dispatch({
                type: DISPLAY_LOADING
            });
            const result = await quanLyDatVeService.layChiTietPhongVe(maLichChieu);
            if (result.status === 200) {
                await dispatch({
                    type: SET_CHI_TIET_PHONG_VE,
                    chiTietPhongVe: result.data.content
                });
            }
            dispatch({ type: HIDE_LOADING });
        } catch (error) {
            console.log('error', error);
        }
    };
};
export const datVeAction = (thongtinDatVe) => {
    return async (dispatch, getState) => {

        try {
            dispatch({
                type: DISPLAY_LOADING
            });
            const result = await quanLyDatVeService.datVe(thongtinDatVe);
            await dispatch(layChiTietPhongVeAction(thongtinDatVe.maLichChieu));
            await dispatch({ type: DAT_VE_HOAN_TAT });

            await dispatch({ type: HIDE_LOADING });
            let userLogin = getState().QuanLyNguoiDungReducer.userLogin;
            connection.invoke('datGheThanhCong', userLogin.taiKhoan, thongtinDatVe.maLichChieu);
            dispatch(thongTinTaiKhoanAction());
            dispatch({
                type: CHUYEN_TAB
            });

        } catch (err) {
            Swal.fire({
                text: 'Cập nhật thành công!',
                icon: 'error',
                showCancelButton: false,
                confirmButtonText: 'Ok',
            }).then((info) => {
            });;

        }

    };
};

export const datGheAction = (ghe, maLichChieu) => {
    return async (dispatch, getState) => {
        //đƯA THÔNG TIN LÊN REDUCER
        await dispatch({
            type: DAT_GHE,
            gheDuocChon: ghe
        });
        //call api ve backend
        let danhSachGheDangDat = getState().QuanLyDatVeReducer.danhSachGheDangDat;
        let taiKhoan = getState().QuanLyNguoiDungReducer.userLogin.taiKhoan;
        // console.log("danhSachGheDangDat", danhSachGheDangDat);
        // console.log("malichchieu", maLichChieu);
        danhSachGheDangDat = JSON.stringify(danhSachGheDangDat);
        taiKhoan = taiKhoan.toString();
        //call api signalR
        connection.invoke('datGhe', taiKhoan, danhSachGheDangDat, maLichChieu);
    };
};
